import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { 
  FaUser, 
  FaBell, 
  FaFileAlt, 
  FaCog, 
  FaSignOutAlt, 
  FaTruck, 
  FaClock, 
  FaMoneyBillWave, 
  FaStore, 
  FaUserNurse,
  FaMedkit,
  FaCheckCircle,
  FaHospital,
  FaInfoCircle
} from "react-icons/fa";
import notificationService from "../../services/notificationService";
import "../../styles/components/user/UserProfile.css";

const UserProfile = () => {
  const [activeTab, setActiveTab] = useState("notifications");
  const [notifications, setNotifications] = useState([]);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  
  const navigate = useNavigate();
  
  useEffect(() => {
    // Check authentication
    const userId = localStorage.getItem('user') 
      ? JSON.parse(localStorage.getItem('user')).userId 
      : sessionStorage.getItem('userId');
    
    const username = localStorage.getItem('user') 
      ? JSON.parse(localStorage.getItem('user')).username 
      : sessionStorage.getItem('username');
    
    if (!userId) {
      navigate('/login');
      return;
    }
    
    // Set basic user info
    setUser({
      userId: userId,
      username: username || "User",
    });
    
    // Fetch notifications
    const fetchNotifications = async () => {
      try {
        setLoading(true);
        const userNotifications = await notificationService.getUserNotifications(userId);
        setNotifications(userNotifications);
      } catch (err) {
        setError("Failed to fetch notifications");
        console.error("Error fetching notifications:", err);
      } finally {
        setLoading(false);
      }
    };
    
    fetchNotifications();
  }, [navigate]);
  
  const handleMarkAsRead = async (notificationId) => {
    try {
      await notificationService.markNotificationAsRead(notificationId);
      // Update local state
      setNotifications(prev => 
        prev.map(item => 
          item.id === notificationId ? { ...item, read: true } : item
        )
      );
    } catch (err) {
      console.error("Error marking notification as read:", err);
    }
  };
  
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleString('en-IN', {
      day: 'numeric',
      month: 'short',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };
  
  const getTimeUntilDelivery = (deliveryTime) => {
    const now = new Date();
    const delivery = new Date(deliveryTime);
    
    if (delivery < now) {
      return "Delivered";
    }
    
    const diffMs = delivery - now;
    const diffMins = Math.round(diffMs / 60000);
    
    if (diffMins <= 0) {
      return "Arriving now";
    } else if (diffMins < 60) {
      return `${diffMins} minutes`;
    } else {
      const hours = Math.floor(diffMins / 60);
      const mins = diffMins % 60;
      return `${hours} hr ${mins} min`;
    }
  };
  
  const renderNotifications = () => {
    if (loading) {
      return (
        <div className="notifications-loading">
          <div className="spinner"></div>
          <p>Loading notifications...</p>
        </div>
      );
    }
    
    if (error) {
      return (
        <div className="notifications-error">
          <FaInfoCircle />
          <p>{error}</p>
          <button onClick={() => window.location.reload()}>Try Again</button>
        </div>
      );
    }
    
    if (notifications.length === 0) {
      return (
        <div className="no-notifications">
          <FaBell className="no-notifications-icon" />
          <h3>No Notifications Yet</h3>
          <p>When you have prescription updates or important messages, they'll appear here.</p>
        </div>
      );
    }
    
    return (
      <div className="notifications-list">
        {notifications.map(notification => (
          <div 
            key={notification.id}
            className={`notification-card ${!notification.read ? 'unread' : ''}`}
          >
            <div className="notification-header">
              <h3>{notification.title}</h3>
              <span className="notification-time">{formatDate(notification.createdAt)}</span>
            </div>
            
            <p className="notification-message">{notification.message}</p>
            
            {notification.type === 'prescription_processed' && (
              <div className="prescription-notification-details">
                <div className="notification-section">
                  <div className="notification-section-header">
                    <FaStore />
                    <h4>Store Information</h4>
                  </div>
                  <p>{notification.store}</p>
                </div>
                
                <div className="notification-section">
                  <div className="notification-section-header">
                    <FaUserNurse />
                    <h4>ASHA Worker</h4>
                  </div>
                  <p>{notification.ashaWorkerName}</p>
                </div>
                
                <div className="notification-delivery-tracker">
                  <div className="notification-section-header">
                    <FaTruck />
                    <h4>Delivery Status</h4>
                  </div>
                  
                  <div className="delivery-status-container">
                    <div className="delivery-progress">
                      <div className="delivery-stage completed">
                        <div className="stage-icon">
                          <FaCheckCircle />
                        </div>
                        <span>Processed</span>
                      </div>
                      
                      <div className="delivery-stage">
                        <div className="stage-icon">
                          <FaHospital />
                        </div>
                        <span>Preparing</span>
                      </div>
                      
                      <div className="delivery-stage">
                        <div className="stage-icon">
                          <FaTruck />
                        </div>
                        <span>On the way</span>
                      </div>
                      
                      <div className="delivery-stage">
                        <div className="stage-icon">
                          <FaCheckCircle />
                        </div>
                        <span>Delivered</span>
                      </div>
                    </div>
                    
                    <div className="delivery-time">
                      <div className="notification-section-header">
                        <FaClock />
                        <h4>Estimated Delivery</h4>
                      </div>
                      <p className="delivery-eta">{getTimeUntilDelivery(notification.estimatedDelivery)}</p>
                    </div>
                  </div>
                </div>
                
                <div className="notification-section">
                  <div className="notification-section-header">
                    <FaMedkit />
                    <h4>Medications</h4>
                  </div>
                  {notification.medications && notification.medications.length > 0 ? (
                    <ul className="medications-list">
                      {notification.medications.map((med, index) => (
                        <li key={index} className="medication-item">
                          <span className="medication-name">{med.name}</span>
                          {med.dosage && <span className="medication-dosage">({med.dosage})</span>}
                        </li>
                      ))}
                    </ul>
                  ) : (
                    <p>Medications as prescribed by your physician</p>
                  )}
                </div>
                
                <div className="notification-section">
                  <div className="notification-section-header">
                    <FaMoneyBillWave />
                    <h4>Billing Information</h4>
                  </div>
                  <div className="billing-details">
                    <div className="billing-row">
                      <span>Medications:</span>
                      <span>₹{notification.billing.medicationCost.toFixed(2)}</span>
                    </div>
                    <div className="billing-row">
                      <span>Delivery Fee:</span>
                      <span>₹{notification.billing.deliveryFee.toFixed(2)}</span>
                    </div>
                    <div className="billing-row total">
                      <span>Total:</span>
                      <span>₹{notification.billing.total.toFixed(2)}</span>
                    </div>
                  </div>
                </div>
              </div>
            )}
            
            <div className="notification-actions">
              {!notification.read && (
                <button 
                  className="mark-read-btn"
                  onClick={() => handleMarkAsRead(notification.id)}
                >
                  Mark as Read
                </button>
              )}
              <button className="view-details-btn">View Details</button>
            </div>
          </div>
        ))}
      </div>
    );
  };
  
  return (
    <div className="user-profile-container">
      <div className="profile-sidebar">
        <div className="profile-user-info">
          <div className="avatar-container">
            <div className="user-avatar">
              <FaUser />
            </div>
            <h2>{user?.username || "User"}</h2>
          </div>
        </div>
        
        <nav className="profile-nav">
          <button
            className={activeTab === "notifications" ? "active" : ""}
            onClick={() => setActiveTab("notifications")}
          >
            <FaBell />
            <span>Notifications</span>
            {notifications.filter(n => !n.read).length > 0 && (
              <span className="badge">{notifications.filter(n => !n.read).length}</span>
            )}
          </button>
          
          <button
            className={activeTab === "prescriptions" ? "active" : ""}
            onClick={() => setActiveTab("prescriptions")}
          >
            <FaFileAlt />
            <span>My Prescriptions</span>
          </button>
          
          <button
            className={activeTab === "settings" ? "active" : ""}
            onClick={() => setActiveTab("settings")}
          >
            <FaCog />
            <span>Settings</span>
          </button>
        </nav>
        
        <div className="profile-sidebar-footer">
          <button className="logout-button">
            <FaSignOutAlt />
            <span>Logout</span>
          </button>
        </div>
      </div>
      
      <div className="profile-content">
        <div className="profile-header">
          <h1>
            {activeTab === "notifications" && "Notifications"}
            {activeTab === "prescriptions" && "My Prescriptions"}
            {activeTab === "settings" && "Settings"}
          </h1>
        </div>
        
        <div className="profile-tab-content">
          {activeTab === "notifications" && (
            <div className="notifications-container">
              <div className="section-header">
                <h2>Recent Updates</h2>
                {notifications.filter(n => !n.read).length > 0 && (
                  <span className="unread-count">
                    {notifications.filter(n => !n.read).length} unread
                  </span>
                )}
              </div>
              {renderNotifications()}
            </div>
          )}
          
          {activeTab === "prescriptions" && (
            <div className="prescriptions-container">
              <p>Your prescription history will appear here.</p>
            </div>
          )}
          
          {activeTab === "settings" && (
            <div className="settings-container">
              <p>Account settings will appear here.</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
