import React, { useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import * as faceapi from 'face-api.js';
import { uploadFaceDescriptor } from './firebase/faceAuthService';
import './Signup.css';

function Signup() {
  const [isCapturing, setIsCapturing] = useState(false);
  const [faceDetected, setFaceDetected] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const videoRef = useRef(null);
  const navigate = useNavigate();

  const startFaceCapture = async () => {
    setIsCapturing(true);
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: {} });
      videoRef.current.srcObject = stream;
    } catch (err) {
      setError('Error accessing camera: ' + err.message);
    }
  };

  const captureFace = async () => {
    if (!videoRef.current) return;

    const detection = await faceapi
      .detectSingleFace(videoRef.current)
      .withFaceLandmarks()
      .withFaceDescriptor();

    if (detection) {
      setFaceDetected(true);
      return detection.descriptor;
    }
    return null;
  };

  const handleSignup = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const faceDescriptor = await captureFace();
      if (!faceDescriptor) {
        throw new Error('No face detected');
      }

      const userId = `user_${Date.now()}`;
      const success = await uploadFaceDescriptor(userId, faceDescriptor);

      if (!success) {
        throw new Error('Failed to save face data');
      }

      stopVideoStream();
      navigate('/login');
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const stopVideoStream = () => {
    if (videoRef.current?.srcObject) {
      videoRef.current.srcObject.getTracks().forEach(track => track.stop());
    }
  };

  return (
    <div className="signup-container">
      <div className="signup-card">
        <h2>Face ID Registration</h2>
        {error && <div className="error-message">{error}</div>}
        
        <form onSubmit={handleSignup}>
          {!isCapturing ? (
            <button 
              type="button" 
              className="face-capture-btn"
              onClick={startFaceCapture}
            >
              Enable Face ID
            </button>
          ) : (
            <div className="video-container">
              <video
                ref={videoRef}
                autoPlay
                muted
              />
              {faceDetected && <div className="face-detected-indicator" />}
            </div>
          )}

          <button 
            type="submit" 
            className="signup-btn"
            disabled={loading || !faceDetected}
          >
            {loading ? 'Registering...' : 'Complete Registration'}
          </button>
        </form>
      </div>
    </div>
  );
}

export default Signup;
