import React, { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import * as faceapi from 'face-api.js';
import { uploadFaceDescriptor } from './firebase/faceAuthService';
import { auth } from './firebase/config';
import { createUserWithEmailAndPassword } from 'firebase/auth';
import './Signup.css';

function Signup() {
  const [isCapturing, setIsCapturing] = useState(false);
  const [faceDetected, setFaceDetected] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [isModelLoaded, setIsModelLoaded] = useState(false);
  const [scanningStatus, setScanningStatus] = useState('');
  const [detectionProgress, setDetectionProgress] = useState(0);
  
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    loadModels();
  }, []);

  const loadModels = async () => {
    try {
      setLoading(true);
      setScanningStatus('Loading face detection models...');
      
      const MODEL_URL = 'https://justadudewhohacks.github.io/face-api.js/models';
      await Promise.all([
        faceapi.nets.tinyFaceDetector.loadFromUri(MODEL_URL),
        faceapi.nets.faceLandmark68Net.loadFromUri(MODEL_URL),
        faceapi.nets.faceRecognitionNet.loadFromUri(MODEL_URL)
      ]);
      
      setIsModelLoaded(true);
      setScanningStatus('Models loaded successfully');
      setLoading(false);
    } catch (err) {
      setError('Error loading face detection models: ' + err.message);
      setLoading(false);
    }
  };

  const startFaceDetection = async () => {
    if (!videoRef.current || !canvasRef.current) return;

    const canvas = canvasRef.current;
    const video = videoRef.current;
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;

    let detectionCount = 0;
    const totalDetectionsNeeded = 20; // Number of samples for better accuracy
    
    const detectFace = async () => {
      setScanningStatus(`Analyzing face characteristics (${Math.round((detectionCount/totalDetectionsNeeded) * 100)}%)`);
      
      try {
        const detection = await faceapi
          .detectSingleFace(video, new faceapi.TinyFaceDetectorOptions())
          .withFaceLandmarks()
          .withFaceDescriptor();

        if (detection) {
          detectionCount++;
          setDetectionProgress(Math.min((detectionCount/totalDetectionsNeeded) * 100, 100));
          
          if (detectionCount >= totalDetectionsNeeded) {
            setFaceDetected(true);
            setScanningStatus('Face successfully detected! Ready to complete registration.');
          }
          
          const context = canvas.getContext('2d');
          context.clearRect(0, 0, canvas.width, canvas.height);
          
          // Draw detection results
          const dims = faceapi.matchDimensions(canvas, video, true);
          const resizedResults = faceapi.resizeResults(detection, dims);
          faceapi.draw.drawDetections(canvas, [resizedResults]);
          faceapi.draw.drawFaceLandmarks(canvas, [resizedResults]);
        } else {
          setScanningStatus('Position your face clearly in the frame');
        }

        if (isCapturing && detectionCount < totalDetectionsNeeded) {
          requestAnimationFrame(detectFace);
        }
      } catch (err) {
        console.error("Detection error:", err);
        setScanningStatus('Error during face detection');
      }
    };

    detectFace();
  };

  const startFaceCapture = async () => {
    setIsCapturing(true);
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ 
        video: { width: 640, height: 480 } 
      });
      
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        videoRef.current.onloadedmetadata = () => {
          videoRef.current.play();
          startFaceDetection();
        };
      }
    } catch (err) {
      setError('Error accessing camera: ' + err.message);
    }
  };

  const handleSignup = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      if (!videoRef.current) {
        throw new Error('Camera not initialized');
      }

      if (!faceDetected) {
        throw new Error('Face not properly detected. Please complete the scanning process.');
      }

      // Create a random user ID for this demo - in real app you'd use Firebase Auth
      const userId = `user_${Date.now()}`;
      
      // Generate email and password for Firebase Auth (example only - should prompt user)
      const demoEmail = `user${Date.now()}@example.com`;
      const demoPassword = `password${Date.now()}`;
      
      try {
        // Create Firebase user
        const userCredential = await createUserWithEmailAndPassword(
          auth, 
          demoEmail, 
          demoPassword
        );
        
        const firebaseUserId = userCredential.user.uid;
        
        // Capture face descriptor
        const detection = await faceapi
          .detectSingleFace(videoRef.current, new faceapi.TinyFaceDetectorOptions())
          .withFaceLandmarks()
          .withFaceDescriptor();

        if (!detection) {
          throw new Error('No face detected in final verification');
        }

        // Upload face descriptor to Firebase
        const success = await uploadFaceDescriptor(firebaseUserId, detection.descriptor);

        if (!success) {
          throw new Error('Failed to save face data');
        }

        setScanningStatus('Registration successful! Redirecting...');
        setTimeout(() => {
          stopVideoStream();
          navigate('/login');
        }, 1500);
        
      } catch (firebaseError) {
        console.error('Firebase error:', firebaseError);
        setError(`Authentication error: ${firebaseError.message}`);
      }
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const stopVideoStream = () => {
    if (videoRef.current?.srcObject) {
      videoRef.current.srcObject.getTracks().forEach(track => track.stop());
    }
  };

  return (
    <div className="signup-container">
      <div className="signup-card">
        <h2>Face ID Registration</h2>
        {error && <div className="error-message">{error}</div>}
        
        <div className="video-wrapper">
          {!isCapturing ? (
            <>
              <button 
                type="button" 
                className="face-capture-btn"
                onClick={startFaceCapture}
                disabled={!isModelLoaded || loading}
              >
                {loading ? 'Loading Models...' : 'Enable Face ID'}
              </button>
              
              {loading && (
                <div className="model-loading-progress">
                  <div className="progress-bar-container">
                    <div className="progress-bar" style={{width: '100%'}}></div>
                  </div>
                  <p className="scanning-status">{scanningStatus}</p>
                </div>
              )}
            </>
          ) : (
            <>
              <div className="video-container">
                <video
                  ref={videoRef}
                  autoPlay
                  muted
                  playsInline
                />
                <canvas ref={canvasRef} className="face-overlay" />
                {faceDetected && <div className="face-detected-indicator" />}
                
                <div className="detection-progress">
                  <div className="progress-bar-container">
                    <div className="progress-bar" style={{width: `${detectionProgress}%`}}></div>
                  </div>
                </div>
              </div>
              
              <div className="detection-status">
                <p className="scanning-status">{scanningStatus}</p>
              </div>
            </>
          )}
        </div>

        <button 
          onClick={handleSignup}
          className="signup-btn"
          disabled={loading || !faceDetected}
        >
          {loading ? 'Registering...' : 'Complete Registration'}
        </button>

        <div className="signup-help">
          <p>Please ensure:</p>
          <ul>
            <li>Your face is well-lit</li>
            <li>Look directly at the camera</li>
            <li>Remove glasses or face coverings</li>
            <li>Keep your face still during scanning</li>
          </ul>
        </div>
      </div>
    </div>
  );
}

export default Signup;
