import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { signOut } from "firebase/auth";
import { ref, get, push, set, update } from "firebase/database";
import { auth, rtdb } from "../../firebase/config";
import { 
  FaUser, 
  FaHome, 
  FaClipboardList, 
  FaUserPlus, 
  FaSignOutAlt, 
  FaUsers, 
  FaMapMarkerAlt, 
  FaTasks, 
  FaExclamationCircle,
  FaChartPie, 
  FaBell, 
  FaCalendarCheck,
  FaCheckCircle,
  FaExclamationTriangle,
  FaPrescriptionBottleAlt,
  FaMapMarked,
  FaMotorcycle,
  FaPhoneAlt,
  FaStore,
  FaRegClock,
  FaTruck,
  FaSearch,
  FaFilter,
  FaMedkit
} from "react-icons/fa";
import { Pie, Bar } from 'react-chartjs-2';
import { Chart as ChartJS, ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement, Title } from 'chart.js';
import "../../styles/components/dashboard/AshaWorkerDashboard.css";
import notificationService from "../../services/notificationService";

// Register ChartJS components
ChartJS.register(ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement, Title);

const AshaWorkerDashboard = () => {
  const [worker, setWorker] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [patients, setPatients] = useState([]);
  const [activeTab, setActiveTab] = useState("dashboard");
  const [isSidebarCollapsed, setSidebarCollapsed] = useState(false);
  const [prescriptions, setPrescriptions] = useState([]);
  const [newPatientData, setNewPatientData] = useState({
    name: "",
    age: "",
    gender: "",
    location: "",
    medicalHistory: "",
    status: "Active"
  });
  const [selectedPrescription, setSelectedPrescription] = useState(null);
  const [showProcessingModal, setShowProcessingModal] = useState(false);
  const [processingSuccess, setProcessingSuccess] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [statusFilter, setStatusFilter] = useState("all");
  const [formErrors, setFormErrors] = useState({});
  const [formSuccess, setFormSuccess] = useState({});
  
  const navigate = useNavigate();

  // Disease data for pie chart
  const diseaseData = {
    labels: [
      'Hypertension', 
      'Diabetes', 
      'Tuberculosis', 
      'Malaria', 
      'Respiratory Infections',
      'Diarrhea'
    ],
    datasets: [
      {
        label: 'Cases',
        data: [65, 59, 42, 31, 56, 25],
        backgroundColor: [
          '#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', '#9966FF',
          '#FF9F40'
        ],
        borderWidth: 1,
      },
    ],
  };
  
  // Monthly data for bar chart
  const monthlyData = {
    labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'],
    datasets: [
      {
        label: 'New Patients',
        data: [12, 19, 15, 25, 22, 30],
        backgroundColor: 'rgba(0, 128, 128, 0.6)',
      },
      {
        label: 'Follow-ups',
        data: [15, 29, 20, 18, 27, 25],
        backgroundColor: 'rgba(54, 162, 235, 0.6)',
      }
    ],
  };
  
  const barOptions = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: 'Monthly Patient Statistics'
      }
    }
  };

  // Chart options
  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'right',
        labels: {
          boxWidth: 12,
          padding: 15,
          font: {
            size: 11
          }
        }
      },
      title: {
        display: true,
        text: 'Common Diseases in Your District',
        font: {
          size: 16
        }
      },
      tooltip: {
        callbacks: {
          label: function(context) {
            const label = context.label || '';
            const value = context.formattedValue || '';
            const total = context.dataset.data.reduce((a, b) => a + b, 0);
            const percentage = Math.round((context.raw / total) * 100);
            return `${label}: ${value} (${percentage}%)`;
          }
        }
      }
    }
  };

  // Mock medical stores data
  const medicalStores = [
    {
      id: 1,
      name: "City Pharmacy",
      address: "123 Main Road, Andheri East, Mumbai",
      phone: "+91 98765 43210",
      distance: "1.2 km",
      deliveryTime: "30-45 min",
      rating: 4.8
    },
    {
      id: 2,
      name: "HealthPlus Medical",
      address: "45 Gandhi Nagar, Goregaon, Mumbai",
      phone: "+91 87654 32109",
      distance: "2.5 km",
      deliveryTime: "45-60 min",
      rating: 4.5
    },
    {
      id: 3,
      name: "LifeCare Pharmacy",
      address: "78 Shastri Nagar, Borivali, Mumbai",
      phone: "+91 76543 21098",
      distance: "3.1 km",
      deliveryTime: "50-70 min",
      rating: 4.3
    }
  ];

  // Upcoming appointments data
  const appointments = [
    {
      id: 1,
      title: "Vaccination Drive",
      location: "Ganeshpuri Community Center",
      date: "2023-06-12",
      time: "10:00 AM",
      count: 25
    },
    {
      id: 2,
      title: "Health Camp",
      location: "Primary School, Malad",
      date: "2023-06-15",
      time: "09:00 AM",
      count: 40
    },
    {
      id: 3,
      title: "Maternal Health Workshop",
      location: "Women's Center, Thane",
      date: "2023-06-20",
      time: "11:00 AM",
      count: 15
    }
  ];

  useEffect(() => {
    console.log("AshaWorkerDashboard mounting...");
    
    // Check if user is logged in via session storage
    const userRole = sessionStorage.getItem('userRole');
    const userId = sessionStorage.getItem('userId');
    
    console.log("Session data:", { userRole, userId });
    
    if (userRole !== 'ashaWorker' || !userId) {
      console.warn("Not authenticated as AshaWorker, redirecting to login");
      navigate("/asha-login");
      return;
    }
    
    // Fetch worker data without listeners
    const fetchWorkerData = async () => {
      try {
        console.log("Fetching worker data for ID:", userId);
        const workerRef = ref(rtdb, `ashaworkers/${userId}`);
        const workerSnapshot = await get(workerRef);
        
        if (workerSnapshot.exists()) {
          const workerData = workerSnapshot.val();
          console.log("Worker data retrieved:", workerData);
          setWorker(workerData);
          await fetchPatients(userId);
        } else {
          console.error("Worker profile not found in database");
          setError("Worker profile not found");
          setTimeout(() => {
            handleLogout();
          }, 3000);
        }
      } catch (err) {
        console.error("Error fetching data:", err);
        setError(`Error: ${err.message}`);
      } finally {
        setLoading(false);
      }
    };
    
    fetchWorkerData();
    fetchPrescriptions();
  }, [navigate]);
  
  // Separate function to fetch patients
  const fetchPatients = async (userId) => {
    try {
      const patientsRef = ref(rtdb, 'patients');
      const patientsSnapshot = await get(patientsRef);
      
      if (patientsSnapshot.exists()) {
        const patientsList = [];
        Object.entries(patientsSnapshot.val()).forEach(([id, data]) => {
          if (data.assignedWorker === userId) {
            patientsList.push({ id, ...data });
          }
        });
        setPatients(patientsList);
      }
    } catch (err) {
      console.error("Error fetching patients:", err);
    }
  };

  // Add fetch prescriptions function
  const fetchPrescriptions = async () => {
    try {
      const prescriptionsRef = ref(rtdb, 'prescriptions');
      const snapshot = await get(prescriptionsRef);
      
      if (snapshot.exists()) {
        const prescriptionList = [];
        Object.entries(snapshot.val()).forEach(([id, data]) => {
          if (data.assignedTo === 'vineetapatil@gmail.com') {
            prescriptionList.push({ id, ...data });
          }
        });
        setPrescriptions(prescriptionList);
      }
    } catch (error) {
      console.error("Error fetching prescriptions:", error);
    }
  };

  const handleLogout = async () => {
    try {
      await signOut(auth);
      sessionStorage.removeItem('userRole');
      sessionStorage.removeItem('userEmail');
      sessionStorage.removeItem('userId');
      sessionStorage.removeItem('workerName');
      sessionStorage.removeItem('workerDistrict');
      navigate("/asha-login");
    } catch (err) {
      setError("Logout failed: " + err.message);
    }
  };

  const validateField = (name, value) => {
    let error = null;
    
    switch(name) {
      case 'name':
        if (!value.trim()) error = "Name is required";
        else if (value.length < 3) error = "Name must be at least 3 characters";
        break;
      case 'age':
        if (!value) error = "Age is required";
        else if (isNaN(value) || value <= 0 || value > 120) error = "Enter a valid age";
        break;
      case 'location':
        if (!value.trim()) error = "Location is required";
        else if (value.length < 5) error = "Enter complete address";
        break;
      // Add other validations as needed
      default:
        break;
    }
    
    return error;
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewPatientData(prev => ({
      ...prev,
      [name]: value
    }));
    
    // Validate
    const error = validateField(name, value);
    if (error) {
      setFormErrors(prev => ({ ...prev, [name]: error }));
      setFormSuccess(prev => ({ ...prev, [name]: false }));
    } else {
      setFormErrors(prev => ({ ...prev, [name]: null }));
      setFormSuccess(prev => ({ ...prev, [name]: true }));
    }
  };

  const handlePatientSubmit = async (e) => {
    e.preventDefault();
    
    if (!newPatientData.name || !newPatientData.age || !newPatientData.gender || !newPatientData.location) {
      setError("Please fill all required fields");
      return;
    }

    try {
      const userId = sessionStorage.getItem('userId'); // Use session storage instead of auth.currentUser
      if (!userId) {
        setError("User session expired. Please login again.");
        setTimeout(() => navigate("/asha-login"), 2000);
        return;
      }
      
      const patientsRef = ref(rtdb, 'patients');
      const newPatientRef = push(patientsRef);
      
      await set(newPatientRef, {
        ...newPatientData,
        assignedWorker: userId,
        registeredDate: new Date().toISOString()
      });
      
      // Reset form
      setNewPatientData({
        name: "",
        age: "",
        gender: "",
        location: "",
        medicalHistory: "",
        status: "Active"
      });
      
      // Refresh patients list
      await fetchPatients(userId);
      
      setActiveTab("patients");
      
    } catch (err) {
      setError("Failed to register patient: " + err.message);
    }
  };

  const handleProcessPrescription = async (prescriptionId, store) => {
    try {
      setLoading(true);
      const prescriptionRef = ref(rtdb, `prescriptions/${prescriptionId}`);
      
      // Generate random estimates for demo purposes
      const estimates = notificationService.generateRandomEstimates();
      
      // Update prescription status
      await update(prescriptionRef, {
        status: 'processing',
        processingDate: new Date().toISOString(),
        processingStore: store.name,
        estimatedDelivery: estimates.deliveryTime,
        processingDetails: {
          storeName: store.name,
          storeAddress: store.address,
          storePhone: store.phone,
          distance: store.distance,
          deliveryTime: estimates.deliveryTime
        },
        billing: estimates.billing
      });
      
      // Get the full prescription data
      const prescription = selectedPrescription;
      
      // Create notification for the user
      await notificationService.createPrescriptionNotification({
        userId: prescription.userId,
        prescriptionId: prescriptionId,
        title: "Your Prescription Has Been Processed",
        message: `Your prescription is being processed by ${worker?.fullName || "an ASHA worker"} and will be delivered soon.`,
        ashaWorkerId: sessionStorage.getItem('userId'),
        ashaWorkerName: worker?.fullName || "ASHA Worker",
        type: "prescription_processed",
        status: "processing",
        store: store.name,
        estimatedDelivery: estimates.deliveryEstimate,
        deliveryTime: estimates.deliveryTime,
        billing: estimates.billing,
        medications: prescription.medications || []
      });
      
      // Refresh prescriptions list
      await fetchPrescriptions();
      
      setProcessingSuccess(true);
      setTimeout(() => {
        setShowProcessingModal(false);
        setProcessingSuccess(false);
        setSelectedPrescription(null);
      }, 2000);
    } catch (error) {
      console.error("Error processing prescription:", error);
      setError("Failed to process prescription");
    } finally {
      setLoading(false);
    }
  };

  const toggleSidebar = () => {
    setSidebarCollapsed(!isSidebarCollapsed);
  };

  const openProcessingModal = (prescription) => {
    setSelectedPrescription(prescription);
    setShowProcessingModal(true);
  };

  const closeProcessingModal = () => {
    setShowProcessingModal(false);
    setProcessingSuccess(false);
  };

  // Get unprocessed prescription count for notification badge
  const unprocessedCount = prescriptions.filter(p => p.status === 'pending').length;

  // Filter prescriptions based on search and status
  const filteredPrescriptions = prescriptions.filter(prescription => {
    const matchesSearch = searchQuery === "" || 
      (prescription.username && prescription.username.toLowerCase().includes(searchQuery.toLowerCase()));
    
    const matchesStatus = statusFilter === "all" || prescription.status === statusFilter;
    
    return matchesSearch && matchesStatus;
  });

  if (loading) {
    return (
      <div className="loading-spinner">
        <div className="spinner"></div>
        <p className="loading-text">Loading dashboard...</p>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      {/* Sidebar */}
      <div className={`dashboard-sidebar ${isSidebarCollapsed ? 'collapsed' : ''}`}>
        <div className="sidebar-toggle" onClick={toggleSidebar}>
          {isSidebarCollapsed ? <span></span> : <span></span>}
        </div>
        
        <div className="sidebar-user">
          <div className="user-avatar">
            <FaUser size={40} />
          </div>
          {!isSidebarCollapsed && (
            <>
              <h3 className="user-name">{worker?.fullName || "Asha Worker"}</h3>
              <p className="user-role">Healthcare Provider</p>
              <p className="user-id">{worker?.workerId || "ID: Unknown"}</p>
            </>
          )}
        </div>
        
        <div className="sidebar-divider"></div>
        
        <nav className="sidebar-nav">
          <ul className="nav-list">
            <li className="nav-item">
              <button 
                className={activeTab === "dashboard" ? "active" : ""}
                onClick={() => setActiveTab("dashboard")}
                title="Dashboard"
              >
                <FaHome />
                {!isSidebarCollapsed && <span>Dashboard</span>}
              </button>
            </li>
            <li className="nav-item">
              <button 
                className={activeTab === "patients" ? "active" : ""}
                onClick={() => setActiveTab("patients")}
                title="Patients"
              >
                <FaClipboardList />
                {!isSidebarCollapsed && <span>Patients</span>}
              </button>
            </li>
            <li className="nav-item">
              <button 
                className={activeTab === "register" ? "active" : ""}
                onClick={() => setActiveTab("register")}
                title="Register Patient"
              >
                <FaUserPlus />
                {!isSidebarCollapsed && <span>Register Patient</span>}
              </button>
            </li>
            <li className="nav-item">
              <button 
                className={activeTab === "prescriptions" ? "active" : ""}
                onClick={() => setActiveTab("prescriptions")}
                title="Prescriptions"
              >
                <div className="icon-with-badge">
                  <FaPrescriptionBottleAlt />
                  {unprocessedCount > 0 && (
                    <span className="badge"></span>
                  )}
                </div>
                {!isSidebarCollapsed && (
                  <span>Prescriptions
                    {unprocessedCount > 0 && !isSidebarCollapsed && (
                      <span className="text-badge">new</span>
                    )}
                  </span>
                )}
              </button>
            </li>
            <li className="nav-item">
              <button 
                className={activeTab === "analytics" ? "active" : ""}
                onClick={() => setActiveTab("analytics")}
                title="Analytics"
              >
                <FaChartPie />
                {!isSidebarCollapsed && <span>Analytics</span>}
              </button>
            </li>
            <li className="nav-item">
              <button 
                className={activeTab === "appointments" ? "active" : ""}
                onClick={() => setActiveTab("appointments")}
                title="Appointments"
              >
                <FaCalendarCheck />
                {!isSidebarCollapsed && <span>Appointments</span>}
              </button>
            </li>
            <li className="nav-item">
              <button 
                className={activeTab === "notifications" ? "active" : ""}
                onClick={() => setActiveTab("notifications")}
                title="Notifications"
              >
                <FaBell />
                {!isSidebarCollapsed && <span>Notifications</span>}
              </button>
            </li>
          </ul>
        </nav>
        
        <div className="sidebar-footer">
          <button className="logout-btn" onClick={handleLogout} title="Logout">
            <FaSignOutAlt />
            {!isSidebarCollapsed && <span>Logout</span>}
          </button>
        </div>
      </div>
      
      {/* Main Content */}
      <div className={`dashboard-content ${isSidebarCollapsed ? 'expanded' : ''}`}>
        <div className="content-header">
          <div className="content-title">
            <h1>
              {activeTab === "dashboard" && "Dashboard"}
              {activeTab === "patients" && "Patient Management"}
              {activeTab === "register" && "Register New Patient"}
              {activeTab === "analytics" && "Analytics & Reports"}
              {activeTab === "appointments" && "Appointments"}
              {activeTab === "notifications" && "Notifications"}
              {activeTab === "prescriptions" && "Prescriptions"}
            </h1>
            <p className="location-info">
              <FaMapMarkerAlt /> {worker?.district || "District"} Area
            </p>
          </div>
          
          <div className="header-actions">
            <span className="date-display">{new Date().toLocaleDateString('en-IN', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' })}</span>
          </div>
        </div>
        
        {error && (
          <div className="error-alert">
            <FaExclamationCircle style={{ marginRight: "10px" }} />
            {error}
            <button className="close-error" onClick={() => setError(null)}></button>
          </div>
        )}
        
        <div className="tab-content">
          {/* Dashboard Content */}
          {activeTab === "dashboard" && (
            <div className="dashboard-view">
              <div className="dash-cards">
                <div className="stat-card">
                  <div className="stat-icon patients-icon">
                    <FaUsers />
                  </div>
                  <div className="stat-info">
                    <h3>Assigned Patients</h3>
                    <p className="stat-value">{patients.length}</p>
                  </div>
                </div>
                
                <div className="stat-card">
                  <div className="stat-icon appointments-icon">
                    <FaCalendarCheck />
                  </div>
                  <div className="stat-info">
                    <h3>Today's Appointments</h3>
                    <p className="stat-value">3</p>
                  </div>
                </div>
                
                <div className="stat-card">
                  <div className="stat-icon prescriptions-icon">
                    <FaPrescriptionBottleAlt />
                  </div>
                  <div className="stat-info">
                    <h3>Pending Prescriptions</h3>
                    <p className="stat-value">{unprocessedCount}</p>
                  </div>
                </div>
                
                <div className="stat-card">
                  <div className="stat-icon tasks-icon">
                    <FaTasks />
                  </div>
                  <div className="stat-info">
                    <h3>Pending Tasks</h3>
                    <p className="stat-value">7</p>
                  </div>
                </div>
              </div>
              
              <div className="dashboard-grid">
                <div className="grid-item chart-container">
                  <h3 className="section-heading">Disease Distribution</h3>
                  <div className="chart-wrapper">
                    <Pie data={diseaseData} options={chartOptions} />
                  </div>
                </div>
                
                <div className="grid-item recent-patients">
                  <h3 className="section-heading">Recent Patients</h3>
                  {patients.length > 0 ? (
                    <div className="recent-list">
                      {patients.slice(0, 5).map(patient => (
                        <div key={patient.id} className="recent-item">
                          <div className="patient-info">
                            <h4>{patient.name}</h4>
                            <p>{patient.age} years  {patient.gender}</p>
                          </div>
                          <span className={`status-badge ${patient.status?.toLowerCase() || 'active'}`}>
                            {patient.status || "Active"}
                          </span>
                        </div>
                      ))}
                    </div>
                  ) : (
                    <p className="no-data">No patients assigned yet</p>
                  )}
                </div>
                
                <div className="grid-item upcoming">
                  <h3 className="section-heading">Upcoming Appointments</h3>
                  <div className="upcoming-list">
                    {appointments.slice(0, 2).map(appointment => (
                      <div key={appointment.id} className="upcoming-item">
                        <div className="upcoming-date">
                          <span className="day">{new Date(appointment.date).getDate()}</span>
                          <span className="month">{new Date(appointment.date).toLocaleString('en-US', {month: 'short'}).toUpperCase()}</span>
                        </div>
                        <div className="upcoming-info">
                          <h4>{appointment.title}</h4>
                          <p>{appointment.location}  {appointment.time}</p>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
                
                <div className="grid-item notifications">
                  <h3 className="section-heading">Recent Notifications</h3>
                  <div className="notification-list">
                    <div className="notification-item">
                      <div className="notification-icon success">
                        <FaCheckCircle />
                      </div>
                      <div className="notification-content">
                        <p className="notification-text">New health guidelines published</p>
                        <p className="notification-time">2 hours ago</p>
                      </div>
                    </div>
                    <div className="notification-item">
                      <div className="notification-icon warning">
                        <FaExclamationTriangle />
                      </div>
                      <div className="notification-content">
                        <p className="notification-text">Reminder: Submit weekly report</p>
                        <p className="notification-time">1 day ago</p>
                      </div>
                    </div>
                    {unprocessedCount > 0 && (
                      <div className="notification-item">
                        <div className="notification-icon urgent">
                          <FaPrescriptionBottleAlt />
                        </div>
                        <div className="notification-content">
                          <p className="notification-text">{unprocessedCount} new prescription{unprocessedCount > 1 ? 's' : ''} to process</p>
                          <p className="notification-time">Just now</p>
                        </div>
                      </div>
                    )}
                  </div>
                </div>
              </div>
            </div>
          )}
          
          {/* Patient List Tab */}
          {activeTab === "patients" && (
            <div className="patients-view">
              <div className="patient-controls">
                <div className="search-filter">
                  <div className="search-input-wrapper">
                    <FaSearch className="search-icon" />
                    <input type="text" placeholder="Search patients..." className="search-input" />
                  </div>
                  <div className="filter-wrapper">
                    <FaFilter className="filter-icon" />
                    <select className="filter-select">
                      <option value="all">All Patients</option>
                      <option value="active">Active</option>
                      <option value="pending">Pending</option>
                      <option value="completed">Completed</option>
                    </select>
                  </div>
                </div>
                <button className="add-patient-btn" onClick={() => setActiveTab("register")}>
                  <FaUserPlus /> Add Patient
                </button>
              </div>
              
              {patients.length > 0 ? (
                <div className="patient-table-container">
                  <table className="patient-table">
                    <thead>
                      <tr>
                        <th>Name</th>
                        <th>Age</th>
                        <th>Gender</th>
                        <th>Location</th>
                        <th>Status</th>
                        <th>Actions</th>
                      </tr>
                    </thead>
                    <tbody>
                      {patients.map(patient => (
                        <tr key={patient.id}>
                          <td>{patient.name}</td>
                          <td>{patient.age}</td>
                          <td>{patient.gender}</td>
                          <td>{patient.location}</td>
                          <td>
                            <span className={`status-badge ${patient.status?.toLowerCase() || 'active'}`}>
                              {patient.status || "Active"}
                            </span>
                          </td>
                          <td>
                            <div className="action-buttons">
                              <button className="action-btn view">View</button>
                              <button className="action-btn edit">Edit</button>
                            </div>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              ) : (
                <div className="no-patients">
                  <div className="empty-state">
                    <FaClipboardList size={48} />
                    <p>No patients assigned yet.</p>
                    <button className="add-patient-btn" onClick={() => setActiveTab("register")}>
                      Register Your First Patient
                    </button>
                  </div>
                </div>
              )}
            </div>
          )}
          
          {/* Register Patient Tab */}
          {activeTab === "register" && (
            <div className="register-view">
              <div className="form-card">
                <h3 className="form-title">Patient Information</h3>
                <form className="reg-form" onSubmit={handlePatientSubmit}>
                  <div className="form-row">
                    <div className="form-group">
                      <label className="form-label">Patient Name</label>
                      <input
                        type="text"
                        name="name"
                        value={newPatientData.name}
                        onChange={handleInputChange}
                        className={`form-input ${formErrors.name ? 'error' : formSuccess.name ? 'success' : ''}`}
                        placeholder="Enter patient name"
                        required
                      />
                      {formErrors.name && (
                        <div className="form-error-text">
                          <FaExclamationCircle /> {formErrors.name}
                        </div>
                      )}
                    </div>
                    
                    <div className="form-group">
                      <label className="form-label">Age</label>
                      <input
                        type="number"
                        name="age"
                        value={newPatientData.age}
                        onChange={handleInputChange}
                        className={`form-input ${formErrors.age ? 'error' : formSuccess.age ? 'success' : ''}`}
                        placeholder="Age"
                        required
                      />
                      {formErrors.age && (
                        <div className="form-error-text">
                          <FaExclamationCircle /> {formErrors.age}
                        </div>
                      )}
                    </div>
                  </div>
                  
                  <div className="form-row">
                    <div className="form-group">
                      <label className="form-label">Gender</label>
                      <select 
                        name="gender"
                        value={newPatientData.gender}
                        onChange={handleInputChange}
                        className="form-select"
                        required
                      >
                        <option value="">Select Gender</option>
                        <option value="Male">Male</option>
                        <option value="Female">Female</option>
                        <option value="Other">Other</option>
                      </select>
                    </div>
                    
                    <div className="form-group">
                      <label className="form-label">Status</label>
                      <select 
                        name="status"
                        value={newPatientData.status}
                        onChange={handleInputChange}
                        className="form-select"
                      >
                        <option value="Active">Active</option>
                        <option value="Pending">Pending</option>
                        <option value="Completed">Completed</option>
                      </select>
                    </div>
                  </div>
                  
                  <div className="form-group">
                    <label className="form-label">Location/Address</label>
                    <input
                      type="text"
                      name="location"
                      value={newPatientData.location}
                      onChange={handleInputChange}
                      className={`form-input ${formErrors.location ? 'error' : formSuccess.location ? 'success' : ''}`}
                      placeholder="Village/Town and Address"
                      required
                    />
                    {formErrors.location && (
                      <div className="form-error-text">
                        <FaExclamationCircle /> {formErrors.location}
                      </div>
                    )}
                  </div>
                  
                  <div className="form-group">
                    <label className="form-label">Medical History</label>
                    <textarea
                      name="medicalHistory"
                      value={newPatientData.medicalHistory}
                      onChange={handleInputChange}
                      className="form-textarea"
                      placeholder="Enter any medical history, conditions, or notes"
                      rows="4"
                    ></textarea>
                  </div>
                  
                  <div className="form-actions">
                    <button type="button" className="cancel-btn" onClick={() => setActiveTab("patients")}>
                      Cancel
                    </button>
                    <button type="submit" className="submit-btn">
                      Register Patient
                    </button>
                  </div>
                </form>
              </div>
            </div>
          )}
          
          {/* Analytics Tab - Improved */}
          {activeTab === "analytics" && (
            <div className="analytics-view">
              <div className="analytics-header">
                <h3>Health Statistics in {worker?.district || "Your District"}</h3>
                <p>Overview of health patterns and disease prevalence in your assigned area.</p>
              </div>
              
              <div className="analytics-cards">
                <div className="analytics-card">
                  <h4>Total Registered Patients</h4>
                  <div className="analytics-value">{patients.length}</div>
                  <div className="analytics-trend positive">+12% this month</div>
                </div>
                <div className="analytics-card">
                  <h4>Active Cases</h4>
                  <div className="analytics-value">{patients.filter(p => p.status === "Active").length || 0}</div>
                  <div className="analytics-trend negative">-5% this month</div>
                </div>
                <div className="analytics-card">
                  <h4>High Risk Patients</h4>
                  <div className="analytics-value">3</div>
                  <div className="analytics-trend neutral">No change</div>
                </div>
                <div className="analytics-card">
                  <h4>Cases This Month</h4>
                  <div className="analytics-value">12</div>
                  <div className="analytics-trend positive">+8% vs last month</div>
                </div>
              </div>
              
              <div className="chart-grid">
                <div className="chart-container analytics-chart">
                  <h3>Disease Distribution</h3>
                  <div className="chart-wrapper large">
                    <Pie data={diseaseData} options={{
                      ...chartOptions,
                      plugins: {
                        ...chartOptions.plugins,
                        legend: {
                          ...chartOptions.plugins.legend,
                          position: 'bottom'
                        }
                      }
                    }} />
                  </div>
                </div>
                
                <div className="chart-container analytics-chart">
                  <h3>Patient Trend</h3>
                  <div className="chart-wrapper large">
                    <Bar data={monthlyData} options={barOptions} />
                  </div>
                </div>
              </div>
              
              <div className="analytics-insights">
                <h3>Key Insights</h3>
                <div className="insights-grid">
                  <div className="insight-card">
                    <div className="insight-icon">
                      <FaChartPie />
                    </div>
                    <div className="insight-content">
                      <h4>Disease Patterns</h4>
                      <p>Hypertension and diabetes remain the most prevalent conditions in your area, accounting for 45% of all cases.</p>
                    </div>
                  </div>
                  
                  <div className="insight-card">
                    <div className="insight-icon">
                      <FaUsers />
                    </div>
                    <div className="insight-content">
                      <h4>Demographics</h4>
                      <p>65% of your patients are women between 25-45 years. Consider targeting health programs for this demographic.</p>
                    </div>
                  </div>
                  
                  <div className="insight-card">
                    <div className="insight-icon">
                      <FaRegClock />
                    </div>
                    <div className="insight-content">
                      <h4>Treatment Duration</h4>
                      <p>Average treatment duration has decreased by 15% due to earlier interventions and better medication adherence.</p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          )}
          
          {/* Appointments Tab - Improved */}
          {activeTab === "appointments" && (
            <div className="appointments-view">
              <div className="appointments-header">
                <div className="appointments-title">
                  <h3>Upcoming Appointments</h3>
                  <p>Manage and schedule community health activities</p>
                </div>
                <button className="create-appointment-btn">
                  <FaCalendarCheck /> New Appointment
                </button>
              </div>
              
              <div className="calendar-container">
                <div className="calendar-header">
                  <button className="calendar-nav-btn">&lt;</button>
                  <h3>June 2023</h3>
                  <button className="calendar-nav-btn">&gt;</button>
                </div>
                <div className="calendar-grid">
                  {/* Calendar display would go here */}
                  <div className="calendar-placeholder">
                    <p>Calendar visualization coming soon</p>
                  </div>
                </div>
              </div>
              
              <div className="appointments-list">
                <h3>Scheduled Events</h3>
                <div className="appointment-cards">
                  {appointments.map(appointment => (
                    <div key={appointment.id} className="appointment-card">
                      <div className="appointment-date">
                        <div className="appointment-day">{new Date(appointment.date).getDate()}</div>
                        <div className="appointment-month">{new Date(appointment.date).toLocaleString('en-US', {month: 'short'})}</div>
                      </div>
                      <div className="appointment-details">
                        <h4>{appointment.title}</h4>
                        <p><FaMapMarkerAlt /> {appointment.location}</p>
                        <p><FaRegClock /> {appointment.time}</p>
                        <div className="appointment-meta">
                          <span className="appointment-attendees">
                            <FaUsers /> {appointment.count} attendees
                          </span>
                        </div>
                      </div>
                      <div className="appointment-actions">
                        <button className="appointment-action edit-btn">Edit</button>
                        <button className="appointment-action delete-btn">Cancel</button>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          )}
          
          {/* Notifications Tab - Improved */}
          {activeTab === "notifications" && (
            <div className="notifications-view">
              <div className="notifications-header">
                <h3>Notifications & Alerts</h3>
                <div className="notifications-actions">
                  <button className="notifications-action mark-all">Mark all as read</button>
                  <button className="notifications-action settings">Settings</button>
                </div>
              </div>
              
              <div className="notifications-tabs">
                <button className="notification-tab active">All</button>
                <button className="notification-tab">Unread</button>
                <button className="notification-tab">Important</button>
              </div>
              
              <div className="notifications-list">
                <div className="notification-group">
                  <h4 className="notification-date">Today</h4>
                  <div className="notification-card unread">
                    <div className="notification-icon success">
                      <FaCheckCircle />
                    </div>
                    <div className="notification-content">
                      <h5>New Health Guidelines Published</h5>
                      <p>Updated COVID-19 protocols for community health workers are now available.</p>
                      <span className="notification-time">2 hours ago</span>
                    </div>
                    <div className="notification-actions">
                      <button className="notification-action">View</button>
                      <button className="notification-action">Mark Read</button>
                    </div>
                  </div>
                  
                  <div className="notification-card unread">
                    <div className="notification-icon urgent">
                      <FaPrescriptionBottleAlt />
                    </div>
                    <div className="notification-content">
                      <h5>New Prescriptions Received</h5>
                      <p>You have {unprocessedCount} new prescription{unprocessedCount !== 1 ? 's' : ''} to process.</p>
                      <span className="notification-time">3 hours ago</span>
                    </div>
                    <div className="notification-actions">
                      <button className="notification-action" onClick={() => setActiveTab("prescriptions")}>Process</button>
                      <button className="notification-action">Mark Read</button>
                    </div>
                  </div>
                </div>
                
                <div className="notification-group">
                  <h4 className="notification-date">Yesterday</h4>
                  <div className="notification-card">
                    <div className="notification-icon warning">
                      <FaExclamationTriangle />
                    </div>
                    <div className="notification-content">
                      <h5>Weekly Report Due</h5>
                      <p>Please submit your weekly patient report by end of day.</p>
                      <span className="notification-time">1 day ago</span>
                    </div>
                    <div className="notification-actions">
                      <button className="notification-action">Submit</button>
                      <button className="notification-action">Dismiss</button>
                    </div>
                  </div>
                </div>
                
                <div className="notification-group">
                  <h4 className="notification-date">Last Week</h4>
                  <div className="notification-card">
                    <div className="notification-icon info">
                      <FaCalendarCheck />
                    </div>
                    <div className="notification-content">
                      <h5>Vaccination Drive Scheduled</h5>
                      <p>New vaccination drive has been scheduled for June 12th.</p>
                      <span className="notification-time">5 days ago</span>
                    </div>
                    <div className="notification-actions">
                      <button className="notification-action">View Details</button>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          )}

          {/* Prescriptions Tab - Enhanced with processing functionality */}
          {activeTab === "prescriptions" && (
            <div className="prescriptions-view">
              <div className="prescriptions-header">
                <div>
                  <h2>Patient Prescriptions</h2>
                  <p>Process and manage patient medication orders</p>
                </div>
                <div className="prescription-filters">
                  <div className="filter-group">
                    <div className="search-input-wrapper">
                      <FaSearch className="search-icon" />
                      <input 
                        type="text" 
                        placeholder="Search by patient name..." 
                        className="search-input"
                        value={searchQuery}
                        onChange={(e) => setSearchQuery(e.target.value)}
                      />
                    </div>
                    <select 
                      className="status-filter form-select" 
                      value={statusFilter}
                      onChange={(e) => setStatusFilter(e.target.value)}
                    >
                      <option value="all">All Status</option>
                      <option value="pending">Pending</option>
                      <option value="processing">Processing</option>
                      <option value="completed">Completed</option>
                    </select>
                  </div>
                </div>
              </div>

              <div className="prescriptions-list">
                {filteredPrescriptions.length > 0 ? (
                  filteredPrescriptions.map(prescription => (
                    <div key={prescription.id} className={`prescription-card ${prescription.status}`}>
                      <div className="prescription-status-indicator">
                        <div className={`status-dot ${prescription.status}`}></div>
                        <span className="status-text">{prescription.status}</span>
                      </div>
                      
                      <div className="prescription-info">
                        <div className="prescription-header-info">
                          <h3>{prescription.username || "Patient"}</h3>
                          <p className="prescription-date">
                            Received: {new Date(prescription.timestamp).toLocaleString()}
                          </p>
                        </div>
                        
                        <div className="patient-details">
                          <p><strong>Patient ID:</strong> {prescription.userId}</p>
                          <p><strong>Address:</strong> {prescription.location || "123, Krishna Nagar, Mumbai"}, Maharashtra, India</p>
                          
                          {prescription.status === 'processing' && (
                            <div className="processing-details">
                              <p><strong>Processing Since:</strong> {new Date(prescription.processingDate).toLocaleString()}</p>
                              <p><strong>Selected Store:</strong> {prescription.processingStore}</p>
                              <p><strong>Est. Delivery:</strong> {prescription.estimatedDelivery}</p>
                            </div>
                          )}
                        </div>
                        
                        <div className="prescription-content">
                          <div className="prescription-section">
                            <h4><FaExclamationCircle /> Symptoms:</h4>
                            <p>{prescription.symptoms || "General symptoms"}</p>
                          </div>
                          
                          <div className="prescription-section">
                            <h4><FaMedkit /> Required Medications:</h4>
                            {prescription.medications && prescription.medications.length > 0 ? (
                              <ul className="medications-list">
                                {prescription.medications.map((med, index) => (
                                  <li key={index} className="medication-item">
                                    <strong>{med.name}</strong> - {med.dosage}
                                  </li>
                                ))}
                              </ul>
                            ) : (
                              <p>General medications as prescribed by physician</p>
                            )}
                          </div>
                        </div>
                      </div>
                      
                      <div className="prescription-actions">
                        {prescription.status === 'pending' && (
                          <button 
                            className="process-btn action-btn" 
                            onClick={() => openProcessingModal(prescription)}
                          >
                            Process Order
                          </button>
                        )}
                        
                        {prescription.status === 'processing' && (
                          <button className="track-btn action-btn">
                            Track Order
                          </button>
                        )}
                        
                        <button className="view-btn action-btn">
                          View Details
                        </button>
                        
                        <button className="contact-btn action-btn">
                          Contact Patient
                        </button>
                      </div>
                    </div>
                  ))
                ) : (
                  <div className="no-prescriptions">
                    <div className="empty-state">
                      <FaPrescriptionBottleAlt size={48} color="#ccc" />
                      <p>No prescriptions found matching your filters</p>
                      {statusFilter !== "all" || searchQuery !== "" ? (
                        <button 
                          className="clear-filters-btn"
                          onClick={() => {
                            setStatusFilter("all");
                            setSearchQuery("");
                          }}
                        >
                          Clear Filters
                        </button>
                      ) : null}
                    </div>
                  </div>
                )}
              </div>
            </div>
          )}
        </div>
      </div>
      
      {/* Processing Modal */}
      {showProcessingModal && selectedPrescription && (
        <div className="modal-overlay">
          <div className="modal-content">
            <button className="modal-close" onClick={closeProcessingModal}></button>
            
            {!processingSuccess ? (
              <>
                <div className="modal-header">
                  <h3>Process Prescription</h3>
                </div>
                <div className="modal-body">
                  <p>Select a medical store to fulfill this prescription:</p>
                  
                  <div className="patient-summary">
                    <strong>Patient:</strong> {selectedPrescription.username || "Unknown patient"}
                    <br />
                    <strong>Location:</strong> {selectedPrescription.location || "Mumbai"}, Maharashtra
                  </div>
                  
                  <div className="stores-list">
                    {medicalStores.map(store => (
                      <div key={store.id} className="store-card">
                        <div className="store-info">
                          <h4>{store.name}</h4>
                          <p><FaMapMarkerAlt /> {store.address}</p>
                          <p><FaPhoneAlt /> {store.phone}</p>
                          <div className="store-meta">
                            <span className="store-distance">
                              <FaMapMarked /> {store.distance}
                            </span>
                            <span className="store-time">
                              <FaTruck /> Delivery: {store.deliveryTime}
                            </span>
                            <span className="store-rating">
                              Rating: {store.rating}/5
                            </span>
                          </div>
                        </div>
                        <button 
                          className="select-store-btn"
                          onClick={() => handleProcessPrescription(selectedPrescription.id, store)}
                          disabled={loading}
                        >
                          {loading ? "Processing..." : "Select Store"}
                        </button>
                      </div>
                    ))}
                  </div>
                </div>
              </>
            ) : (
              <div className="success-message">
                <div className="success-icon">
                  <FaCheckCircle />
                </div>
                <h3>Prescription Processed!</h3>
                <p>The order has been sent to the pharmacy and is being processed.</p>
                <p>The patient will be notified about their medication delivery details.</p>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default AshaWorkerDashboard;
