import React, { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import * as faceapi from 'face-api.js';
import { registerUser, updateFaceAuthStatus } from './firebase/authService';
import { saveFaceDescriptor } from './services/localFaceStorage';
import './Signup.css';
import { checkBrowserCompatibility, testCameraAccess } from './utils/browserCheck';
import { saveFaceData, captureFaceImage } from './services/faceDataService';
import { requestCameraPermission } from './utils/cameraDebugger';

function Signup() {
  // Form state
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [username, setUsername] = useState('');
  const [formValid, setFormValid] = useState(false);
  
  // Face capture state
  const [isCapturing, setIsCapturing] = useState(false);
  const [showFaceSetup, setShowFaceSetup] = useState(true); // Start with face setup
  const [faceDetected, setFaceDetected] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [isModelLoaded, setIsModelLoaded] = useState(false);
  const [scanningStatus, setScanningStatus] = useState('');
  const [detectionProgress, setDetectionProgress] = useState(0);
  const [browserSupport, setBrowserSupport] = useState(null);
  const [faceDescriptor, setFaceDescriptor] = useState(null); // Store face descriptor for later use
  
  // Additional state for diagnostics
  const [cameraStatus, setCameraStatus] = useState("Not initialized");
  const [modelStatus, setModelStatus] = useState("Not loaded");
  const [diagnosticInfo, setDiagnosticInfo] = useState({});
  const [showRegistrationForm, setShowRegistrationForm] = useState(false); // Control when to show registration form

  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const navigate = useNavigate();

  // Validate form inputs
  useEffect(() => {
    const isValid = 
      email.length > 0 && 
      email.includes('@') && 
      password.length >= 6 && 
      password === confirmPassword &&
      username.length > 0;
    setFormValid(isValid);
  }, [email, password, confirmPassword, username]);

  // Load face-api models when component mounts
  useEffect(() => {
    loadModels();
  }, []);

  // Check browser compatibility when component mounts
  useEffect(() => {
    const compatResult = checkBrowserCompatibility();
    setBrowserSupport(compatResult);
    if (!compatResult.compatible) {
      setError(`Browser compatibility issues detected: ${compatResult.issues.join(", ")}`);
    }
  }, []);

  // Add useEffect to detect if camera is already running
  useEffect(() => {
    if (isModelLoaded && videoRef.current?.srcObject) {
      // Camera is already running, update status
      setCameraStatus("Camera is active");
    } else if (isModelLoaded && !videoRef.current?.srcObject && isCapturing) {
      // Camera should be running but isn't
      setCameraStatus("Camera initialization in progress...");
    }
  }, [isModelLoaded, videoRef.current?.srcObject, isCapturing]);

  // Add a useEffect to display a prompt when models are loaded
  useEffect(() => {
    if (isModelLoaded && !isCapturing) {
      // Models are loaded but camera not started yet
      setCameraStatus("Camera ready! Click 'Enable Camera' to start");
    }
  }, [isModelLoaded, isCapturing]);

  const MODEL_URL = 'https://justadudewhohacks.github.io/face-api.js/models';
  const loadModels = async () => {
    try {
      setLoading(true);
      setModelStatus("Loading models...");
      setScanningStatus('Loading face detection models...');
      
      await faceapi.nets.tinyFaceDetector.loadFromUri(MODEL_URL);
      setModelStatus("Loading TinyFaceDetector...");
      
      await faceapi.nets.faceLandmark68Net.loadFromUri(MODEL_URL);
      setModelStatus("Loading FaceLandmark68...");
      
      await faceapi.nets.faceRecognitionNet.loadFromUri(MODEL_URL);
      setModelStatus("Loading FaceRecognition...");
      
      setIsModelLoaded(true);
      setModelStatus("All models loaded successfully");
      setScanningStatus('Models loaded successfully');
      
      // Camera status should be updated to indicate readiness for camera
      setCameraStatus("Ready for camera access");
      setLoading(false);
    } catch (err) {
      setModelStatus(`Error loading models: ${err.message}`);
      setError('Error loading face detection models: ' + err.message);
      setLoading(false);
    }
  };

  const startFaceDetection = async () => {
    if (!videoRef.current || !canvasRef.current) return;
    const totalDetectionsNeeded = 20; // Number of samples for better accuracy
    const canvas = canvasRef.current;
    const video = videoRef.current;
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;

    let detectionCount = 0;
    let lastDetection = null;

    const detectFace = async () => {
      setScanningStatus(`Analyzing face characteristics (${Math.round((detectionCount/totalDetectionsNeeded) * 100)}%)`);
      
      try {
        const detection = await faceapi
          .detectSingleFace(video, new faceapi.TinyFaceDetectorOptions())
          .withFaceLandmarks()
          .withFaceDescriptor();

        if (detection) {
          detectionCount++;
          lastDetection = detection; // Save for later use
          setDetectionProgress(Math.min((detectionCount/totalDetectionsNeeded) * 100, 100));
          
          if (detectionCount >= totalDetectionsNeeded) {
            setFaceDetected(true);
            setFaceDescriptor(detection.descriptor); // Save the descriptor
            setScanningStatus('Face successfully detected! Continue to complete registration.');
          }

          // Draw detection results
          const dims = faceapi.matchDimensions(canvas, video, true);
          const resizedResults = faceapi.resizeResults(detection, dims);
          faceapi.draw.drawDetections(canvas, [resizedResults]);
          faceapi.draw.drawFaceLandmarks(canvas, [resizedResults]);
        } else {
          setScanningStatus('Position your face clearly in the frame');
        }

        if (isCapturing && detectionCount < totalDetectionsNeeded) {
          requestAnimationFrame(detectFace);
        }
      } catch (err) {
        console.error("Detection error:", err);
        setScanningStatus('Error during face detection');
      }
    };

    detectFace();
  };

  // Modify startFaceCapture to explicitly force camera permission prompt
  const startFaceCapture = async () => {
    setIsCapturing(true);
    setCameraStatus("Initializing camera...");
    
    try {
      // Add explicit permission request code to force the permission dialog
      console.log("Explicitly requesting camera permission...");
      setCameraStatus("Requesting camera permission...");
      
      // First try to directly get camera permission using the Permissions API
      try {
        if (navigator.permissions && navigator.permissions.query) {
          const permissionStatus = await navigator.permissions.query({ name: 'camera' });
          console.log("Current camera permission status:", permissionStatus.state);
          setCameraStatus(`Detected camera permission: ${permissionStatus.state}`);
        }
      } catch (permErr) {
        console.log("Permission API check failed:", permErr);
      }
      
      // Now force the permission prompt with getUserMedia directly
      console.log("Forcing camera permission prompt...");
      setCameraStatus("Waiting for camera permission...");
      
      // Force a simpler getUserMedia call first to trigger the permission prompt
      const initialStream = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: false
      });
      
      // Log success and stop this initial stream
      console.log("Initial camera permission granted!");
      initialStream.getTracks().forEach(track => track.stop());
      
      // Now proceed with the full camera setup with our preferred constraints
      console.log("Setting up camera with preferred constraints...");
      setCameraStatus("Camera permission granted, setting up video...");
      
      // Explicitly set video constraints for better compatibility
      const constraints = {
        audio: false,
        video: {
          facingMode: "user",
          width: { ideal: 640, min: 320 },
          height: { ideal: 480, min: 240 }
        }
      };
      
      const stream = await navigator.mediaDevices.getUserMedia(constraints);
      console.log("Camera stream obtained:", stream);
      setCameraStatus("Camera stream active");
      
      // Check if stream is actually active
      if (!stream.active) {
        throw new Error("Stream is not active even though permission was granted");
      }
      
      // Check if we have video tracks
      const videoTracks = stream.getVideoTracks();
      if (videoTracks.length === 0) {
        throw new Error("No video tracks in the media stream");
      }
      
      console.log(`Got ${videoTracks.length} video tracks:`, 
        videoTracks.map(t => t.label).join(', '));
      
      // Update diagnostic info
      setDiagnosticInfo({
        streamActive: stream.active,
        streamId: stream.id,
        videoTracks: stream.getVideoTracks().map(track => ({
          label: track.label,
          enabled: track.enabled,
          muted: track.muted
        }))
      });
      
      // Connect stream to video element
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        videoRef.current.style.display = "block";
        // ...rest of video element styling...
        
        // Rest of the video event setup
        // ...existing code...
      }
      // ...existing error handling...
    } catch (err) {
      console.error("Camera access error:", err);
      setCameraStatus(`Camera access error: ${err.name} - ${err.message}`);
      setError(`Camera access denied: ${err.message}. Please ensure camera permissions are granted and try again.`);
      setIsCapturing(false);
    }
  };

  // Continue to the registration form after face detection
  const continueToRegistration = () => {
    // Stop video stream to conserve resources
    stopVideoStream();
    // Show the registration form
    setShowRegistrationForm(true);
  };

  // Modified handleSignup to use the stored face descriptor
  const handleSignup = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    try {
      // Register the user with Firebase (or local fallback)
      const userData = await registerUser(email, password, username, true);
      const userId = userData.userId;
      
      // Save the already captured face data
      if (faceDescriptor) {
        console.log("Saving face data for user:", username);
        
        // 1. Save face descriptor data (both in localStorage and as a file)
        const success = await saveFaceData(userId, username, faceDescriptor);
        
        if (success) {
          // Update Firebase to indicate this user has face auth enabled
          await updateFaceAuthStatus(userId, true);
          console.log("Face data saved successfully and status updated in Firebase");
        } else {
          console.error("Failed to save face data");
          throw new Error('Failed to save face ID data');
        }
      } else {
        console.error("No face descriptor available");
        throw new Error('Face identification data is missing');
      }

      // Show success message
      alert('Account created successfully! You can now log in.');
      navigate('/login');
    } catch (err) {
      console.error('Signup error:', err);
      setError('Error creating account: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  const stopVideoStream = () => {
    if (videoRef.current?.srcObject) {
      videoRef.current.srcObject.getTracks().forEach(track => track.stop());
    }
  };

  const skipFaceSetup = () => {
    if (window.confirm('Are you sure you want to skip Face ID setup? You can still log in with email and password.')) {
      setShowRegistrationForm(true);
      setFaceDetected(false);
      stopVideoStream();
    }
  };

  return (
    <div className="signup-container">
      <div className="signup-card">
        <h2>Create Account</h2>
        {error && <div className="error-message">{error}</div>}
        
        {showRegistrationForm ? (
          // Email/password registration form (shown after face setup)
          <form onSubmit={handleSignup}>
            <div className="form-group">
              <label htmlFor="username">Username</label>
              <input 
                id="username"
                type="text"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                placeholder="Choose a username"
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="email">Email Address</label>
              <input 
                id="email"
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="Enter your email"
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="password">Password</label>
              <input 
                id="password"
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Create a password"
                required
                minLength={6}
              />
              {password.length > 0 && password.length < 6 && (
                <div className="input-hint">Password must be at least 6 characters</div>
              )}
            </div>
            <div className="form-group">
              <label htmlFor="confirmPassword">Confirm Password</label>
              <input 
                id="confirmPassword"
                type="password"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
                placeholder="Confirm your password"
                required
              />
              {confirmPassword.length > 0 && confirmPassword !== password && (
                <div className="input-hint">Passwords don't match</div>
              )}
            </div>
            
            {/* Show face detection status in registration form */}
            <div className="face-auth-status">
              <div className="status-badge success">
                <span className="checkmark"></span>
                Face ID Setup Complete
              </div>
            </div>
            
            <button 
              type="submit" 
              className="signup-btn continue-btn"
              disabled={!formValid || loading}
            >
              {loading ? 'Creating Account...' : 'Complete Registration'}
            </button>
            
            <div className="login-link">
              Already have an account? <a href="/login">Sign in</a>
            </div>
          </form>
        ) : (
          <div className="face-setup">
            <div className="setup-header">
              <h3>Set Up Face ID</h3>
              <p className="setup-description">
                Let's set up Face ID first. This will enable quick and secure login later.
              </p>
            </div>
            
            {/* Status indicators */}
            <div className="setup-status">
              <div className="status-item">
                <span className="status-label">Camera:</span>
                <span className={`status-value ${cameraStatus.includes("error") || cameraStatus.includes("denied") ? "status-error" : 
                    cameraStatus.includes("active") ? "" : "status-waiting"}`}>
                  {cameraStatus}
                </span>
              </div>
              <div className="status-item">
                <span className="status-label">Models:</span>
                <span className={`status-value ${modelStatus.includes("Error") ? "status-error" : ""}`}>
                  {modelStatus}
                </span>
              </div>
              {loading && (
                <div className="model-loading-progress">
                  <div className="progress-bar-container">
                    <div className="progress-bar" style={{width: '100%'}}></div>
                  </div>
                  <p className="scanning-status">{scanningStatus}</p>
                </div>
              )}
            </div>

            <div className="video-wrapper">
              {!isCapturing ? (
                <>
                  <button 
                    type="button" 
                    className="face-capture-btn primary-btn camera-btn-enhanced"
                    onClick={startFaceCapture}
                    disabled={!isModelLoaded || loading}
                  >
                    {loading ? 'Loading Models...' : 'ðŸ“· Enable Camera Access Now'}
                  </button>
                  
                  {/* Add stronger instructions */}
                  {isModelLoaded && !loading && (
                    <div className="camera-instructions enhanced">
                      <p><strong>Action required:</strong> Click the button above to enable camera access.</p>
                      <p>A browser permission dialog will appear - you must click "Allow" to continue.</p>
                    </div>
                  )}
                </>
              ) : (
                <>
                  <div className="video-container" style={{
                    position: 'relative',
                    width: '100%',
                    height: '400px',
                    overflow: 'hidden',
                    borderRadius: '12px',
                    backgroundColor: '#000', // Dark background makes the video more visible
                    border: '3px solid teal',
                    margin: '20px 0',
                    boxShadow: '0 10px 25px rgba(0, 128, 128, 0.3)'
                  }}>
                    <video 
                      ref={videoRef}
                      autoPlay
                      playsInline
                      muted
                      style={{
                        width: '100%',
                        height: '100%',
                        objectFit: 'cover',
                        display: 'block',
                        zIndex: 1
                      }}
                    />
                    <canvas 
                      ref={canvasRef} 
                      className="face-overlay" 
                      style={{
                        position: 'absolute',
                        top: 0,
                        left: 0,
                        width: '100%',
                        height: '100%',
                        zIndex: 2
                      }}
                    />
                    {faceDetected && <div className="face-detected-indicator" />}
                    
                    {/* Camera feed status overlay */}
                    <div style={{
                      position: 'absolute',
                      bottom: '10px',
                      left: '10px',
                      right: '10px',
                      padding: '8px',
                      background: 'rgba(0,0,0,0.6)',
                      color: 'white',
                      fontSize: '14px',
                      borderRadius: '4px',
                      textAlign: 'center',
                      zIndex: 3
                    }}>
                      {videoRef.current?.srcObject ? 
                        `Camera active - ${scanningStatus || "Position your face in the frame"}` : 
                        'Initializing camera...'}
                    </div>
                  </div>
                  
                  <div className="detection-status">
                    <div className="progress-bar-container">
                      <div className="progress-bar" style={{width: `${detectionProgress}%`}}></div>
                    </div>
                    <p className="scanning-status">{scanningStatus || "Waiting for face detection..."}</p>
                    <p className="face-status">{faceDetected ? 'Face Detected âœ“' : 'No Face Detected'}</p>
                  </div>
                </>
              )}
            </div>

            {isCapturing && (
              <div className="face-setup-actions">
                <button 
                  className="skip-btn"
                  type="button"
                  onClick={skipFaceSetup}
                >
                  Skip Face ID
                </button>
                
                <button 
                  onClick={continueToRegistration}
                  className="signup-btn"
                  disabled={loading || !faceDetected}
                >
                  {loading ? 'Processing...' : !faceDetected ? 'Face Detection Required' : 'Continue to Registration'}
                </button>
              </div>
            )}

            {isCapturing && (
              <div className="signup-help">
                <p>Please ensure:</p>
                <ul>
                  <li>Your face is well-lit</li>
                  <li>Look directly at the camera</li>
                  <li>Remove glasses or face coverings</li>
                  <li>Keep your face still during scanning</li>
                </ul>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default Signup;
