import React, { useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import * as faceapi from 'face-api.js';
import { getFaceDescriptor } from './services/localStorageService';
import './Login.css';

const Login = () => {
  const [isCapturing, setIsCapturing] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const videoRef = useRef(null);
  const navigate = useNavigate();

  const startFaceAuthentication = async () => {
    setIsCapturing(true);
    try {
      const MODEL_URL = 'https://justadudewhohacks.github.io/face-api.js/models';
      await Promise.all([
        faceapi.nets.tinyFaceDetector.loadFromUri(MODEL_URL),
        faceapi.nets.faceLandmark68Net.loadFromUri(MODEL_URL),
        faceapi.nets.faceRecognitionNet.loadFromUri(MODEL_URL)
      ]);

      const stream = await navigator.mediaDevices.getUserMedia({ video: {} });
      videoRef.current.srcObject = stream;
    } catch (err) {
      setError('Error accessing camera: ' + err.message);
    }
  };

  const verifyFace = async () => {
    if (!videoRef.current) return;

    try {
      setLoading(true);
      const detection = await faceapi
        .detectSingleFace(videoRef.current, new faceapi.TinyFaceDetectorOptions())
        .withFaceLandmarks()
        .withFaceDescriptor();

      if (!detection) {
        throw new Error('No face detected');
      }

      // Get stored face descriptors
      const storedUsers = JSON.parse(localStorage.getItem('user_data') || '[]');
      
      for (const user of storedUsers) {
        const storedDescriptor = await getFaceDescriptor(user.userId);
        if (storedDescriptor) {
          const distance = faceapi.euclideanDistance(detection.descriptor, storedDescriptor);
          if (distance < 0.6) { // Threshold for face match
            setIsAuthenticated(true);
            stopVideoStream();
            localStorage.setItem('currentUser', JSON.stringify(user));
            navigate('/');
            return;
          }
        }
      }
      
      setError('Face not recognized. Please try again or register.');
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const stopVideoStream = () => {
    if (videoRef.current?.srcObject) {
      videoRef.current.srcObject.getTracks().forEach(track => track.stop());
    }
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <h2>Face Authentication</h2>
        {error && <div className="error-message">{error}</div>}
        
        <div className="auth-content">
          {!isCapturing ? (
            <button 
              className="face-auth-btn"
              onClick={startFaceAuthentication}
              disabled={loading}
            >
              Start Face Authentication
            </button>
          ) : (
            <div className="video-container">
              <video
                ref={videoRef}
                autoPlay
                muted
                onPlay={verifyFace}
              />
              {loading && <div className="scanning-overlay">
                <div className="scanning-animation"></div>
                <p>Scanning...</p>
              </div>}
            </div>
          )}
        </div>

        <div className="alternative-auth">
          <p>Don't have an account?</p>
          <button 
            className="register-btn"
            onClick={() => navigate('/signup')}
          >
            Register with Face ID
          </button>
        </div>
      </div>
    </div>
  );
};

export default Login;
