import React, { useState, useEffect, useRef } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import * as faceapi from 'face-api.js';
import './Login.css';
import { getAllFaceDescriptors } from './services/localFaceStorage';
import { findMatchingFace, formatMatchConfidence } from './utils/faceUtils';
import { testCameraWithConstraints } from './utils/cameraDebugger';

function Login() {
  // State hooks
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [loginMethod, setLoginMethod] = useState('face');
  const [faceAuthStage, setFaceAuthStage] = useState('start');
  const [cameraReady, setCameraReady] = useState(false);
  const [modelsLoaded, setModelsLoaded] = useState(false);
  const [scanProgress, setScanProgress] = useState(0);
  const [matchConfidence, setMatchConfidence] = useState(null);
  const [matchedUser, setMatchedUser] = useState(null);
  
  // Refs
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const streamRef = useRef(null);
  const detectionIntervalRef = useRef(null);
  
  // Router hooks
  const navigate = useNavigate();
  const location = useLocation();
  
  // Set up face detection on component mount
  useEffect(() => {
    const loadModels = async () => {
      try {
        // Set paths to models - adjust these paths according to your project structure
        const MODEL_URL = process.env.PUBLIC_URL + '/models';
        
        await Promise.all([
          faceapi.nets.tinyFaceDetector.loadFromUri(MODEL_URL),
          faceapi.nets.faceLandmark68Net.loadFromUri(MODEL_URL),
          faceapi.nets.faceRecognitionNet.loadFromUri(MODEL_URL)
        ]);
        
        console.log('Face detection models loaded');
        setModelsLoaded(true);
      } catch (error) {
        console.error('Error loading face-api models:', error);
        setError('Failed to load facial recognition models. Please try again or use email login.');
      }
    };

    loadModels();
    
    // Clean up on unmount
    return () => {
      if (streamRef.current) {
        streamRef.current.getTracks().forEach(track => track.stop());
      }
      
      if (detectionIntervalRef.current) {
        clearInterval(detectionIntervalRef.current);
      }
    };
  }, []);

  // Function to handle face authentication
  const startFaceAuth = async () => {
    setError(null);
    setFaceAuthStage('scanning');
    
    try {
      // Request camera access
      const constraints = {
        video: {
          width: { ideal: 640 },
          height: { ideal: 480 },
          facingMode: 'user'
        }
      };
      
      // Test camera with these constraints
      const testResult = await testCameraWithConstraints(constraints);
      if (!testResult.success) {
        throw new Error(`Camera access failed: ${testResult.errorMessage || 'Unknown error'}`);
      }
      
      const stream = await navigator.mediaDevices.getUserMedia(constraints);
      streamRef.current = stream;
      
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        videoRef.current.play();
        setCameraReady(true);
        
        // Start face detection after camera is ready
        videoRef.current.onloadedmetadata = () => {
          detectFaces();
        };
      }
    } catch (err) {
      console.error('Error starting camera:', err);
      setError(`Camera access error: ${err.message || 'Unknown error'}. Please try email login.`);
      setFaceAuthStage('start');
    }
  };

  // Function to detect faces
  const detectFaces = () => {
    if (!videoRef.current || !modelsLoaded) {
      return;
    }

    // Clear any existing detection interval
    if (detectionIntervalRef.current) {
      clearInterval(detectionIntervalRef.current);
    }

    let detectionCount = 0;
    const requiredDetections = 5;
    let accumulatedDescriptor = null;

    detectionIntervalRef.current = setInterval(async () => {
      try {
        if (videoRef.current && videoRef.current.readyState === 4) {
          const detections = await faceapi.detectSingleFace(
            videoRef.current, 
            new faceapi.TinyFaceDetectorOptions()
          ).withFaceLandmarks().withFaceDescriptor();

          if (detections) {
            // Update progress
            detectionCount++;
            const progress = Math.min((detectionCount / requiredDetections) * 100, 100);
            setScanProgress(progress);

            // Accumulate descriptor for better accuracy
            if (!accumulatedDescriptor) {
              accumulatedDescriptor = detections.descriptor;
            } else {
              // Merge descriptors by averaging
              for (let i = 0; i < 128; i++) {
                accumulatedDescriptor[i] = (accumulatedDescriptor[i] * (detectionCount - 1) + detections.descriptor[i]) / detectionCount;
              }
            }

            // Draw face detection on canvas if we have a canvas ref
            if (canvasRef.current) {
              const displaySize = { 
                width: videoRef.current.width, 
                height: videoRef.current.height 
              };
              faceapi.matchDimensions(canvasRef.current, displaySize);
              
              const resizedDetection = faceapi.resizeResults(detections, displaySize);
              const context = canvasRef.current.getContext('2d');
              context.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);
              faceapi.draw.drawDetections(canvasRef.current, resizedDetection);
            }

            // If we have enough detections, try to match face
            if (detectionCount >= requiredDetections) {
              clearInterval(detectionIntervalRef.current);
              matchFace(accumulatedDescriptor);
            }
          }
        }
      } catch (err) {
        console.error('Face detection error:', err);
        clearInterval(detectionIntervalRef.current);
        setError(`Face detection error: ${err.message}`);
        setFaceAuthStage('start');
      }
    }, 500);
  };

  // Function to match detected face with stored faces
  const matchFace = async (descriptor) => {
    try {
      // Get all stored face descriptors
      const faceData = getAllFaceDescriptors();
      
      if (!faceData || faceData.length === 0) {
        setError('No registered faces found. Please sign up or use email login.');
        setFaceAuthStage('start');
        return;
      }
      
      // Find a match
      const match = findMatchingFace(descriptor, faceData);
      
      if (match) {
        const { user, confidence } = match;
        console.log('Match found:', user.username, 'Confidence:', confidence);
        
        setMatchConfidence(confidence);
        setMatchedUser(user);
        setFaceAuthStage('success');
        
        // Stop video stream
        if (streamRef.current) {
          streamRef.current.getTracks().forEach(track => track.stop());
        }
        
        // After 2 seconds, redirect to home page
        setTimeout(() => {
          handleSuccessfulFaceLogin(user);
        }, 2000);
      } else {
        setError('Face not recognized. Please try again or use email login.');
        setFaceAuthStage('start');
      }
    } catch (err) {
      console.error('Error during face matching:', err);
      setError(`Authentication error: ${err.message}`);
      setFaceAuthStage('start');
    }
  };

  // Function to handle successful face login
  const handleSuccessfulFaceLogin = (matchedUser) => {
    if (!matchedUser || !matchedUser.username) {
      setError("Authentication failed. User data incomplete.");
      return;
    }

    console.log("Face authentication successful for:", matchedUser.username);
    
    // Store user session data in sessionStorage
    sessionStorage.setItem('authenticated', 'true');
    sessionStorage.setItem('userId', matchedUser.userId || matchedUser.user?.userId);
    sessionStorage.setItem('username', matchedUser.username);
    sessionStorage.setItem('loginMethod', 'face');
    sessionStorage.setItem('loginTime', new Date().toISOString());
    
    // Optionally store some user data in localStorage for persistence
    try {
      localStorage.setItem('user', JSON.stringify({
        userId: matchedUser.userId || matchedUser.user?.userId,
        username: matchedUser.username,
        role: 'user',
        lastLogin: new Date().toISOString()
      }));
    } catch (e) {
      console.warn("Could not save to localStorage:", e);
    }
    
    // Use the proper location from useLocation()
    const redirectTo = location.state?.redirectAfterLogin || "/";
    navigate(redirectTo);
  };

  // Function to handle email login form submission
  const handleEmailLogin = (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    
    // Simulate API call
    setTimeout(() => {
      // Simple validation
      if (email === 'test@example.com' && password === 'password') {
        // Store login info in session storage
        sessionStorage.setItem('authenticated', 'true');
        sessionStorage.setItem('userId', 'user_' + Date.now());
        sessionStorage.setItem('username', email.split('@')[0]);
        sessionStorage.setItem('loginMethod', 'email');
        navigate('/');
      } else {
        setError('Invalid email or password');
        setLoading(false);
      }
    }, 1000);
  };

  // Function to cancel face auth and go back to start
  const cancelFaceAuth = () => {
    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
    }
    
    if (detectionIntervalRef.current) {
      clearInterval(detectionIntervalRef.current);
    }
    
    setFaceAuthStage('start');
    setCameraReady(false);
    setScanProgress(0);
  };

  // Function to retry face auth
  const retryFaceAuth = () => {
    cancelFaceAuth();
    setTimeout(() => {
      startFaceAuth();
    }, 500);
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <h2>Login</h2>
        
        {/* Toggle between login methods */}
        <div className="login-methods">
          <button 
            className={`method-btn ${loginMethod === 'face' ? 'active' : ''}`}
            onClick={() => setLoginMethod('face')}
          >
            Face Login
          </button>
          <button 
            className={`method-btn ${loginMethod === 'email' ? 'active' : ''}`}
            onClick={() => setLoginMethod('email')}
          >
            Email Login
          </button>
        </div>

        {error && <div className="error-message">{error}</div>}
        
        {/* Face Authentication UI */}
        {loginMethod === 'face' && (
          <div className="face-auth-container">
            {faceAuthStage === 'start' && (
              <div className="login-start-section">
                <div className="face-icon-container">
                  <div className="face-icon"></div>
                </div>
                <p className="login-instruction">
                  Use facial recognition to login instantly
                </p>
                <button 
                  className="start-face-auth-btn"
                  onClick={startFaceAuth}
                  disabled={!modelsLoaded}
                >
                  {modelsLoaded ? 'Start Face Authentication' : 'Loading...'}
                </button>
              </div>
            )}
            
            {faceAuthStage === 'scanning' && (
              <>
                <div className="video-container">
                  <video
                    ref={videoRef}
                    autoPlay
                    playsInline
                    muted
                    width="100%"
                    height="100%"
                  />
                  <canvas ref={canvasRef} className="face-canvas" />
                  
                  <div className="scanning-overlay">
                    <div className="scanning-animation"></div>
                    <p className="scan-status">Scanning your face...</p>
                    
                    <div className="progress-container">
                      <div 
                        className="progress-bar" 
                        style={{ width: `${scanProgress}%` }}
                      ></div>
                    </div>
                  </div>
                </div>
                
                <div className="auth-action-buttons">
                  <button className="retry-button" onClick={retryFaceAuth}>
                    Retry
                  </button>
                  <button className="cancel-button" onClick={cancelFaceAuth}>
                    Cancel
                  </button>
                </div>
              </>
            )}
            
            {faceAuthStage === 'success' && (
              <div className="video-container">
                <div className="auth-success-overlay">
                  <div className="success-checkmark"></div>
                  <h3>Authentication Successful</h3>
                  {matchConfidence && (
                    <p>Match confidence: {formatMatchConfidence(matchConfidence)}</p>
                  )}
                  <p className="redirect-message">Redirecting you...</p>
                </div>
              </div>
            )}
            
            <div className="alternative-auth">
              <p>Don't have an account?</p>
              <button 
                className="register-btn"
                onClick={() => navigate('/signup')}
              >
                Register
              </button>
            </div>
          </div>
        )}
        
        {/* Email Login Form */}
        {loginMethod === 'email' && (
          <form onSubmit={handleEmailLogin} className="login-form">
            <div className="form-group">
              <label>Email</label>
              <input 
                type="email" 
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </div>
            
            <div className="form-group">
              <label>Password</label>
              <input 
                type="password" 
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            
            <div className="form-options">
              <label className="remember-me">
                <input type="checkbox" /> Remember me
              </label>
              <a href="#forgot-password" className="forgot-password">
                Forgot Password?
              </a>
            </div>
            
            <button 
              type="submit" 
              className={`login-btn ${loading ? 'loading' : ''}`}
              disabled={loading}
            >
              {loading ? 'Logging in...' : 'Login'}
            </button>
            
            <div className="signup-link">
              Don't have an account? <a href="/signup">Sign up</a>
            </div>
          </form>
        )}
      </div>
    </div>
  );
}

export default Login;
