import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "../../styles/Navbar.css";
import { FaBars, FaTimes, FaCaretDown, FaCaretUp, FaUser, FaSignOutAlt } from "react-icons/fa";
import { getLoggedInUser, logoutUser, isAshaWorker } from "../../utils/authUtils";

function Navbar() {
  const [user, setUser] = useState(null);
  const [isScrolled, setIsScrolled] = useState(false);
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [showAshaDropdown, setShowAshaDropdown] = useState(false);
  const [showUserDropdown, setShowUserDropdown] = useState(false);
  const navigate = useNavigate();
  
  // Function to check authentication status - called on mount and after actions
  const checkAuthStatus = () => {
    const currentUser = getLoggedInUser();
    if (currentUser) {
      setUser(currentUser);
    } else {
      setUser(null);
    }
  };
  
  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };

    window.addEventListener('scroll', handleScroll);
    
    // Check authentication status on component mount
    checkAuthStatus();

    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const closeMenu = () => {
    setIsMenuOpen(false);
  };

  const toggleAshaDropdown = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setShowAshaDropdown(prev => !prev);
    setShowUserDropdown(false);
  };

  const toggleUserDropdown = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setShowUserDropdown(prev => !prev);
    setShowAshaDropdown(false);
  };

  const handleLogout = () => {
    // Determine if user is an Asha worker and log out accordingly
    if (isAshaWorker()) {
      logoutUser('ashaWorker');
    } else {
      logoutUser('regular');
    }
    
    setUser(null);
    setShowUserDropdown(false);
    closeMenu();
    navigate("/");
  };

  useEffect(() => {
    const closeDropdowns = () => {
      setShowAshaDropdown(false);
      setShowUserDropdown(false);
    };
    
    if (showAshaDropdown || showUserDropdown) {
      document.addEventListener('click', closeDropdowns);
    }
    
    return () => {
      document.removeEventListener('click', closeDropdowns);
    };
  }, [showAshaDropdown, showUserDropdown]);

  return (
    <nav className={`navbar ${isScrolled ? 'scroll-nav' : ''}`}>
      <div className="navbar-container">
        <Link to="/" className="navbar-logo" onClick={closeMenu}>
          <div className="logo-typewriter">
            <span className="typewriter-text">mediconnect</span>
            <span className="typewriter-cursor">.</span>
          </div>
        </Link>
        <button className="menu-toggle" onClick={toggleMenu} aria-label="Toggle menu">
          {isMenuOpen ? <FaTimes /> : <FaBars />}
        </button>
        <ul className={`navbar-links ${isMenuOpen ? 'active' : ''}`}>
          <li><Link to="/" onClick={closeMenu}>Home</Link></li>
          <li><Link to="/about" onClick={closeMenu}>About</Link></li>
          <li><Link to="/services" onClick={closeMenu}>Services</Link></li>
          <li>
            <a href="https://esanjeevani.mohfw.gov.in/#/" target="_blank" rel="noopener noreferrer" onClick={closeMenu}>
              Sanjeevani App
            </a>
          </li>
          
          {/* Asha Worker Dropdown */}
          <li className="dropdown-item" onClick={(e) => e.stopPropagation()}>
            <a href="#!" className="dropdown-toggle" onClick={toggleAshaDropdown}>
              Asha Worker {showAshaDropdown ? <FaCaretUp /> : <FaCaretDown />}
            </a>
            {showAshaDropdown && (
              <div className="dropdown-menu">
                <Link to="/asha-login" className="dropdown-link" onClick={closeMenu}>
                  Login
                </Link>
                <Link to="/asha-signup" className="dropdown-link" onClick={closeMenu}>
                  Register
                </Link>
                <Link to="/asha-dashboard" className="dropdown-link" onClick={closeMenu}>
                  Dashboard
                </Link>
              </div>
            )}
          </li>
          
          {/* Conditional render based on auth state */}
          {!user ? (
            <li><Link to="/login" onClick={closeMenu}>Login</Link></li>
          ) : (
            <>
              <li><Link to="/chatbot" onClick={closeMenu}>Chatbot</Link></li>
              <li><Link to="/video-recommendations" onClick={closeMenu}>Video Recommendations</Link></li>
              
              {/* User Profile Dropdown */}
              <li className="dropdown-item user-dropdown" onClick={(e) => e.stopPropagation()}>
                <a href="#!" className="dropdown-toggle user-toggle" onClick={toggleUserDropdown}>
                  <FaUser /> {user.username} {showUserDropdown ? <FaCaretUp /> : <FaCaretDown />}
                </a>
                {showUserDropdown && (
                  <div className="dropdown-menu user-menu">
                    {user.role === 'ashaWorker' ? (
                      <Link to="/asha-dashboard" className="dropdown-link" onClick={closeMenu}>
                        ASHA Dashboard
                      </Link>
                    ) : (
                      <Link to="/prescription" className="dropdown-link" onClick={closeMenu}>
                        My Prescriptions
                      </Link>
                    )}
                    <button className="dropdown-link logout-link" onClick={handleLogout}>
                      <FaSignOutAlt /> Logout
                    </button>
                  </div>
                )}
              </li>
            </>
          )}
        </ul>
      </div>
    </nav>
  );
}

export default Navbar;
