import React, { useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import * as faceapi from 'face-api.js';
import { getFaceDescriptor } from './firebase/faceAuthService';
import './Login.css';

const Login = () => {
  const [isCapturing, setIsCapturing] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const videoRef = useRef(null);
  const navigate = useNavigate();

  const startFaceAuthentication = async () => {
    setIsCapturing(true);
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: {} });
      videoRef.current.srcObject = stream;
    } catch (err) {
      setError('Error accessing camera: ' + err.message);
    }
  };

  const verifyFace = async () => {
    if (!videoRef.current) return;

    try {
      setLoading(true);
      const detection = await faceapi
        .detectSingleFace(videoRef.current, new faceapi.TinyFaceDetectorOptions())
        .withFaceLandmarks()
        .withFaceDescriptor();

      if (!detection) {
        throw new Error('No face detected');
      }

      const storedDescriptor = await getFaceDescriptor('user_123'); // Replace with actual user ID
      if (storedDescriptor) {
        const distance = faceapi.euclideanDistance(detection.descriptor, storedDescriptor);
        if (distance < 0.6) { // Threshold for face match
          setIsAuthenticated(true);
          stopVideoStream();
          navigate('/');
          return;
        }
      }

      setError('Face not recognized. Please try again or register.');
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const stopVideoStream = () => {
    if (videoRef.current?.srcObject) {
      videoRef.current.srcObject.getTracks().forEach(track => track.stop());
    }
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <h2>Face Authentication</h2>
        {error && <div className="error-message">{error}</div>}
        
        <div className="auth-content">
          {!isCapturing ? (
            <button 
              className="face-auth-btn"
              onClick={startFaceAuthentication}
            >
              Start Face Authentication
            </button>
          ) : (
            <div className="video-container">
              <video
                ref={videoRef}
                autoPlay
                muted
                onPlay={verifyFace}
              />
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Login;
