import React, { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";
import { FaUser, FaLock } from "react-icons/fa";
import { signInWithEmailAndPassword, signOut } from "firebase/auth"; // Added signOut import
import { ref, get } from "firebase/database"; // Import RTDB methods
import { auth, rtdb } from "./firebase/config"; // Import rtdb

const AshaWorkerLogin = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // Check if already logged in
  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged((user) => {
      if (user && sessionStorage.getItem('userRole') === 'ashaWorker') {
        navigate("/asha-dashboard");
      }
    });

    return () => unsubscribe();
  }, [navigate]);

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      console.log("Attempting login with:", email);
      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      const user = userCredential.user;
      
      console.log("Login successful, fetching worker data...");
      
      // Fetch worker data from RTDB
      const workerRef = ref(rtdb, `ashaworkers/${user.uid}`);
      const workerSnapshot = await get(workerRef);
      
      if (workerSnapshot.exists()) {
        const workerData = workerSnapshot.val();
        
        // Store essential worker data in session storage
        sessionStorage.setItem('userRole', 'ashaWorker');
        sessionStorage.setItem('userEmail', email);
        sessionStorage.setItem('userId', user.uid);
        sessionStorage.setItem('workerName', workerData.fullName);
        sessionStorage.setItem('workerDistrict', workerData.district);
        
        console.log("Worker data retrieved:", workerData.fullName);
        
        // Navigate to asha dashboard
        navigate("/asha-dashboard");
      } else {
        console.error("Worker data not found in database");
        setError("Account exists but worker profile is missing. Please contact support.");
        await signOut(auth); // Sign out since profile is missing
        setLoading(false);
      }
    } catch (error) {
      console.error("Login error:", error);
      setError(getErrorMessage(error.code));
      setLoading(false);
    }
  };

  // Helper function to provide user-friendly error messages
  const getErrorMessage = (errorCode) => {
    switch (errorCode) {
      case 'auth/invalid-email':
        return 'Invalid email address format.';
      case 'auth/user-disabled':
        return 'This account has been disabled.';
      case 'auth/user-not-found':
        return 'No account found with this email.';
      case 'auth/wrong-password':
        return 'Incorrect password.';
      case 'auth/too-many-requests':
        return 'Too many unsuccessful login attempts. Try again later.';
      default:
        return 'Failed to login. Please try again.';
    }
  };

  return (
    <div
      style={{
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        minHeight: "100vh",
        backgroundImage: "url('https://via.placeholder.com/1920x1080?text=Healthcare+Background')",
        backgroundSize: "cover",
        backgroundPosition: "center",
      }}
    >
      <div
        style={{
          backgroundColor: "rgba(255, 255, 255, 0.8)",
          padding: "2.5rem",
          borderRadius: "15px",
          boxShadow: "0 8px 20px rgba(0, 0, 0, 0.2)",
          width: "400px",
          textAlign: "center",
          backdropFilter: "blur(10px)",
          border: "1px solid rgba(255, 255, 255, 0.3)",
          transition: "transform 0.3s ease, box-shadow 0.3s ease",
        }}
        onMouseOver={(e) => {
          e.currentTarget.style.transform = "scale(1.02)";
          e.currentTarget.style.boxShadow = "0 12px 24px rgba(0, 0, 0, 0.3)";
        }}
        onMouseOut={(e) => {
          e.currentTarget.style.transform = "scale(1)";
          e.currentTarget.style.boxShadow = "0 8px 20px rgba(0, 0, 0, 0.2)";
        }}
      >
        <h2
          style={{
            fontSize: "2rem",
            fontWeight: "bold",
            marginBottom: "1.5rem",
            color: "#2d3748",
            textShadow: "2px 2px 4px rgba(0, 0, 0, 0.1)",
          }}
        >
          Asha Worker Login
        </h2>
        
        {error && (
          <div style={{
            padding: "10px",
            marginBottom: "15px",
            backgroundColor: "rgba(254, 215, 215, 0.8)",
            color: "#c53030",
            borderRadius: "8px",
            fontSize: "0.9rem"
          }}>
            {error}
          </div>
        )}
        
        <form onSubmit={handleLogin}>
          <div style={{ marginBottom: "1.5rem", position: "relative" }}>
            <label
              style={{
                display: "block",
                fontSize: "0.875rem",
                fontWeight: "medium",
                marginBottom: "0.5rem",
                color: "#4a5568",
                textAlign: "left"
              }}
            >
              Email
            </label>
            <div style={{ position: "relative" }}>
              <FaUser
                style={{
                  position: "absolute",
                  left: "10px",
                  top: "50%",
                  transform: "translateY(-50%)",
                  color: "#718096",
                }}
              />
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                style={{
                  width: "100%",
                  padding: "0.5rem 0.5rem 0.5rem 2.5rem",
                  border: "1px solid #e2e8f0",
                  borderRadius: "0.375rem",
                  outline: "none",
                  transition: "border-color 0.2s, box-shadow 0.2s",
                }}
                onFocus={(e) => {
                  e.target.style.borderColor = "#4299e1";
                  e.target.style.boxShadow = "0 0 0 3px rgba(66, 153, 225, 0.2)";
                }}
                onBlur={(e) => {
                  e.target.style.borderColor = "#e2e8f0";
                  e.target.style.boxShadow = "none";
                }}
                required
                disabled={loading}
              />
            </div>
          </div>
          <div style={{ marginBottom: "2rem", position: "relative" }}>
            <label
              style={{
                display: "block",
                fontSize: "0.875rem",
                fontWeight: "medium",
                marginBottom: "0.5rem",
                color: "#4a5568",
                textAlign: "left"
              }}
            >
              Password
            </label>
            <div style={{ position: "relative" }}>
              <FaLock
                style={{
                  position: "absolute",
                  left: "10px",
                  top: "50%",
                  transform: "translateY(-50%)",
                  color: "#718096",
                }}
              />
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                style={{
                  width: "100%",
                  padding: "0.5rem 0.5rem 0.5rem 2.5rem",
                  border: "1px solid #e2e8f0",
                  borderRadius: "0.375rem",
                  outline: "none",
                  transition: "border-color 0.2s, box-shadow 0.2s",
                }}
                onFocus={(e) => {
                  e.target.style.borderColor = "#4299e1";
                  e.target.style.boxShadow = "0 0 0 3px rgba(66, 153, 225, 0.2)";
                }}
                onBlur={(e) => {
                  e.target.style.borderColor = "#e2e8f0";
                  e.target.style.boxShadow = "none";
                }}
                required
                disabled={loading}
              />
            </div>
          </div>
          <button
            type="submit"
            disabled={loading}
            style={{
              width: "100%",
              backgroundColor: loading ? "#90cdf4" : "#4299e1",
              color: "white",
              padding: "0.75rem",
              borderRadius: "0.375rem",
              fontWeight: "bold",
              cursor: loading ? "not-allowed" : "pointer",
              transition: "background-color 0.2s, transform 0.2s",
              border: "none",
              display: "flex",
              justifyContent: "center",
              alignItems: "center"
            }}
            onMouseOver={(e) => {
              if (!loading) {
                e.target.style.backgroundColor = "#3182ce";
                e.target.style.transform = "translateY(-2px)";
              }
            }}
            onMouseOut={(e) => {
              if (!loading) {
                e.target.style.backgroundColor = "#4299e1";
                e.target.style.transform = "translateY(0)";
              }
            }}
          >
            {loading ? "Logging in..." : "Login"}
          </button>
        </form>
        
        <div style={{ marginTop: "20px", color: "#4a5568" }}>
          <p>Don't have an account? <Link to="/asha-signup" style={{ color: "#4299e1", textDecoration: "none", fontWeight: "500" }}>Sign Up</Link></p>
        </div>
      </div>
    </div>
  );
};

export default AshaWorkerLogin;
