import React, { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import * as faceapi from 'face-api.js';
import { saveFaceDescriptor, saveUserData } from './services/localStorageService';
import './Signup.css';

function Signup() {
  // Form state
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  
  // UI state
  const [step, setStep] = useState(1); // 1: Email/Password, 2: Face Capture
  const [isCapturing, setIsCapturing] = useState(false);
  const [faceDetected, setFaceDetected] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [isModelLoaded, setIsModelLoaded] = useState(false);
  const [scanningStatus, setScanningStatus] = useState('');
  const [detectionProgress, setDetectionProgress] = useState(0);
  const [skipFaceId, setSkipFaceId] = useState(false);
  
  // Refs
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Load face-api models on component mount
    loadModels();
  }, []);

  const loadModels = async () => {
    try {
      setLoading(true);
      setScanningStatus('Loading face detection models...');
      
      const MODEL_URL = 'https://justadudewhohacks.github.io/face-api.js/models';
      await Promise.all([
        faceapi.nets.tinyFaceDetector.loadFromUri(MODEL_URL),
        faceapi.nets.faceLandmark68Net.loadFromUri(MODEL_URL),
        faceapi.nets.faceRecognitionNet.loadFromUri(MODEL_URL)
      ]);
      
      setIsModelLoaded(true);
      setScanningStatus('Models loaded successfully');
      setLoading(false);
    } catch (err) {
      console.error("Error loading face models:", err);
      setError('Error loading face detection models: ' + err.message);
      setLoading(false);
    }
  };

  const validateEmailPassword = () => {
    // Basic email validation
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) {
      setError('Please enter a valid email address');
      return false;
    }

    // Password validation
    if (password.length < 6) {
      setError('Password must be at least 6 characters');
      return false;
    }

    // Password match validation
    if (password !== confirmPassword) {
      setError('Passwords do not match');
      return false;
    }

    return true;
  };

  const handleEmailPasswordSubmit = (e) => {
    e.preventDefault();
    setError('');
    
    if (!validateEmailPassword()) {
      return;
    }

    // Move to face capture step or complete registration if skipping
    if (skipFaceId) {
      handleSignupWithoutFace();
    } else {
      setStep(2);
    }
  };

  const handleSignupWithoutFace = async () => {
    setLoading(true);
    try {
      const userId = `user_${Date.now()}`;
      
      // Save user data to localStorage
      const saveSuccess = await saveUserData({
        userId,
        email,
        password, // In a real app, this should be hashed
        createdAt: new Date().toISOString(),
        hasFaceAuth: false
      });
      
      if (!saveSuccess) {
        throw new Error('Failed to save user data');
      }
      
      // Success - redirect to login
      navigate('/login', { 
        state: { 
          message: 'Account created successfully! Please log in.' 
        } 
      });
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const startFaceCapture = async () => {
    setIsCapturing(true);
    try {
      console.log("Requesting camera access...");
      const stream = await navigator.mediaDevices.getUserMedia({
        video: {
          facingMode: "user",
          width: { ideal: 640 },
          height: { ideal: 480 }
        }
      });
      
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        
        videoRef.current.onloadedmetadata = () => {
          videoRef.current.play()
            .then(() => {
              startFaceDetection();
            })
            .catch(err => {
              setError(`Failed to play video: ${err.message}. Please try again.`);
            });
        };
      } else {
        setError("Video element not available. Please refresh the page.");
      }
    } catch (err) {
      console.error("Camera access error:", err);
      setError(`Camera access denied: ${err.message}. Please allow camera access and try again.`);
    }
  };

  const startFaceDetection = async () => {
    if (!videoRef.current || !canvasRef.current) return;

    const canvas = canvasRef.current;
    const video = videoRef.current;
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;

    let detectionCount = 0;
    const totalDetectionsNeeded = 20; // Number of samples for better accuracy
    
    const detectFace = async () => {
      setScanningStatus(`Analyzing face characteristics (${Math.round((detectionCount/totalDetectionsNeeded) * 100)}%)`);
      
      try {
        const detection = await faceapi
          .detectSingleFace(video, new faceapi.TinyFaceDetectorOptions())
          .withFaceLandmarks()
          .withFaceDescriptor();

        if (detection) {
          detectionCount++;
          setDetectionProgress(Math.min((detectionCount/totalDetectionsNeeded) * 100, 100));
          
          if (detectionCount >= totalDetectionsNeeded) {
            setFaceDetected(true);
            setScanningStatus('Face successfully detected! Ready to complete registration.');
          }
          
          const context = canvas.getContext('2d');
          context.clearRect(0, 0, canvas.width, canvas.height);
          
          // Draw detection results for visual feedback
          const dims = faceapi.matchDimensions(canvas, video, true);
          const resizedResults = faceapi.resizeResults(detection, dims);
          faceapi.draw.drawDetections(canvas, [resizedResults]);
          faceapi.draw.drawFaceLandmarks(canvas, [resizedResults]);
        } else {
          setScanningStatus('Position your face clearly in the frame');
        }

        if (isCapturing && detectionCount < totalDetectionsNeeded) {
          requestAnimationFrame(detectFace);
        }
      } catch (err) {
        console.error("Face detection error:", err);
        setScanningStatus('Error during face detection');
      }
    };

    detectFace();
  };

  const handleSignupWithFace = async () => {
    setLoading(true);
    setError('');

    try {
      if (!videoRef.current || !videoRef.current.srcObject) {
        throw new Error('Camera not initialized properly. Please try again.');
      }

      if (!faceDetected) {
        throw new Error('Face not properly detected. Please complete the scanning process.');
      }

      // Create a unique user ID
      const userId = `user_${Date.now()}`;
      
      // Capture final face data
      const detection = await faceapi
        .detectSingleFace(videoRef.current, new faceapi.TinyFaceDetectorOptions())
        .withFaceLandmarks()
        .withFaceDescriptor();

      if (!detection) {
        throw new Error('Face detection failed in final verification. Please try again.');
      }

      // Save face descriptor locally
      const descriptorSaved = await saveFaceDescriptor(userId, detection.descriptor);
      if (!descriptorSaved) {
        throw new Error('Failed to save face data');
      }
      
      // Save user data (email, password, etc.)
      const userSaved = await saveUserData({
        userId,
        email,
        password, // In a real app, this should be hashed
        createdAt: new Date().toISOString(),
        hasFaceAuth: true
      });
      
      if (!userSaved) {
        throw new Error('Failed to save user data');
      }

      // Cleanup and redirect
      stopVideoStream();
      navigate('/login', { 
        state: { 
          message: 'Account created successfully with Face ID! Please log in.' 
        } 
      });
      
    } catch (err) {
      console.error('Signup error:', err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const stopVideoStream = () => {
    if (videoRef.current?.srcObject) {
      videoRef.current.srcObject.getTracks().forEach(track => track.stop());
    }
  };

  const goBack = () => {
    // Stop video stream if active
    if (isCapturing) {
      stopVideoStream();
    }
    setStep(1);
    setIsCapturing(false);
    setFaceDetected(false);
  };

  return (
    <div className="signup-container">
      <div className="signup-card">
        <h2>{step === 1 ? "Create Account" : "Set Up Face ID"}</h2>
        {error && <div className="error-message">{error}</div>}
        
        {step === 1 ? (
          /* Step 1: Email/Password Form */
          <form onSubmit={handleEmailPasswordSubmit} className="signup-form">
            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input
                type="email"
                id="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="Enter your email"
                required
              />
            </div>
            
            <div className="form-group">
              <label htmlFor="password">Password</label>
              <input
                type="password"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Create a password"
                required
              />
            </div>
            
            <div className="form-group">
              <label htmlFor="confirmPassword">Confirm Password</label>
              <input
                type="password"
                id="confirmPassword"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
                placeholder="Confirm your password"
                required
              />
            </div>

            <div className="checkbox-group">
              <input 
                type="checkbox" 
                id="skipFaceId" 
                checked={skipFaceId}
                onChange={(e) => setSkipFaceId(e.target.checked)}
              />
              <label htmlFor="skipFaceId">Skip Face ID setup (you can add it later)</label>
            </div>
            
            <button 
              type="submit" 
              className="signup-btn"
              disabled={loading}
            >
              {loading ? 'Processing...' : skipFaceId ? 'Create Account' : 'Continue to Face ID'}
            </button>
            
            <div className="login-link">
              Already have an account? <span onClick={() => navigate('/login')}>Log in</span>
            </div>
          </form>
        ) : (
          /* Step 2: Face ID Setup */
          <div className="face-setup-container">
            <div className="video-wrapper">
              {!isCapturing ? (
                <>
                  <button 
                    type="button" 
                    className="face-capture-btn"
                    onClick={startFaceCapture}
                    disabled={!isModelLoaded || loading}
                  >
                    {loading ? 'Loading Models...' : 'Start Face ID Setup'}
                  </button>
                  
                  {loading && (
                    <div className="model-loading-progress">
                      <div className="progress-bar-container">
                        <div className="progress-bar" style={{width: '100%'}}></div>
                      </div>
                      <p className="scanning-status">{scanningStatus}</p>
                    </div>
                  )}
                </>
              ) : (
                <>
                  <div className="video-container">
                    <video
                      ref={videoRef}
                      autoPlay
                      playsInline
                      muted
                      style={{
                        width: '100%',
                        height: 'auto',
                        maxHeight: '400px',
                        objectFit: 'cover',
                        backgroundColor: '#f0f0f0'
                      }}
                    />
                    <canvas 
                      ref={canvasRef} 
                      className="face-overlay" 
                      style={{
                        position: 'absolute',
                        top: 0,
                        left: 0,
                        width: '100%',
                        height: '100%'
                      }}
                    />
                    {faceDetected && <div className="face-detected-indicator" />}
                  </div>
                  
                  <div className="detection-status">
                    <div className="progress-bar-container">
                      <div className="progress-bar" style={{width: `${detectionProgress}%`}}></div>
                    </div>
                    <p className="scanning-status">{scanningStatus || "Waiting for face detection..."}</p>
                    <p className="face-status">{faceDetected ? 'Face Detected ✓' : 'No Face Detected'}</p>
                  </div>
                </>
              )}
            </div>

            <div className="face-setup-actions">
              <button 
                type="button"
                className="back-btn"
                onClick={goBack}
                disabled={loading}
              >
                Back
              </button>
              <button 
                type="button"
                className="signup-btn"
                onClick={handleSignupWithFace}
                disabled={loading || !faceDetected}
              >
                {loading ? 'Creating Account...' : 'Complete Registration'}
              </button>
            </div>

            <div className="signup-help">
              <p>Please ensure:</p>
              <ul>
                <li>Your face is well-lit</li>
                <li>Look directly at the camera</li>
                <li>Remove glasses or face coverings</li>
                <li>Keep your face still during scanning</li>
              </ul>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default Signup;
