import React, { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import * as faceapi from 'face-api.js';
import './Signup.css';

function Signup() {
  // State for face registration
  const [username, setUsername] = useState('');
  const [isCapturing, setIsCapturing] = useState(false);
  const [faceDetected, setFaceDetected] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [isModelLoaded, setIsModelLoaded] = useState(false);
  const [scanningStatus, setScanningStatus] = useState('');
  const [detectionProgress, setDetectionProgress] = useState(0);
  const [faceDescriptor, setFaceDescriptor] = useState(null); // Store face descriptor for registration
  const [registrationComplete, setRegistrationComplete] = useState(false);
  
  // References and navigation
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const navigate = useNavigate();

  // Load face detection models when component mounts
  useEffect(() => {
    loadModels();
  }, []);

  // Function to load face-api models
  const MODEL_URL = 'https://justadudewhohacks.github.io/face-api.js/models';
  const loadModels = async () => {
    try {
      setLoading(true);
      setScanningStatus('Loading face detection models...');
      
      await faceapi.nets.tinyFaceDetector.loadFromUri(MODEL_URL);
      await faceapi.nets.faceLandmark68Net.loadFromUri(MODEL_URL);
      await faceapi.nets.faceRecognitionNet.loadFromUri(MODEL_URL);
      
      setIsModelLoaded(true);
      setScanningStatus('Models loaded successfully');
      setLoading(false);
    } catch (err) {
      setError('Error loading face detection models: ' + err.message);
      setLoading(false);
    }
  };

  // Start camera and face detection
  const startFaceCapture = async () => {
    if (!username.trim()) {
      setError('Please enter a username first');
      return;
    }
    
    setIsCapturing(true);
    setError('');
    
    try {
      // Simple camera access
      const stream = await navigator.mediaDevices.getUserMedia({video: true});
      
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        
        videoRef.current.onloadedmetadata = () => {
          videoRef.current.play()
            .then(() => {
              startFaceDetection();
            })
            .catch(err => {
              setError("Error playing video: " + err.message);
              setIsCapturing(false);
            });
        };
      }
    } catch (err) {
      setError("Camera access failed: " + err.message);
      setIsCapturing(false);
    }
  };

  // Detect face in video stream
  const startFaceDetection = async () => {
    if (!videoRef.current || !canvasRef.current) return;
    
    const totalDetectionsNeeded = 20; // Number of samples for better accuracy
    const canvas = canvasRef.current;
    const video = videoRef.current;
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;

    let detectionCount = 0;

    const detectFace = async () => {
      setScanningStatus(`Analyzing face characteristics (${Math.round((detectionCount/totalDetectionsNeeded) * 100)}%)`);
      
      try {
        const detection = await faceapi
          .detectSingleFace(video, new faceapi.TinyFaceDetectorOptions())
          .withFaceLandmarks()
          .withFaceDescriptor();

        if (detection) {
          detectionCount++;
          setDetectionProgress(Math.min((detectionCount/totalDetectionsNeeded) * 100, 100));
          
          // Draw detection results
          const dims = faceapi.matchDimensions(canvas, video, true);
          const resizedResults = faceapi.resizeResults(detection, dims);
          canvas.getContext('2d').clearRect(0, 0, canvas.width, canvas.height);
          faceapi.draw.drawDetections(canvas, [resizedResults]);
          faceapi.draw.drawFaceLandmarks(canvas, [resizedResults]);
          
          if (detectionCount >= totalDetectionsNeeded) {
            setFaceDetected(true);
            setFaceDescriptor(detection.descriptor);
            setScanningStatus('Face successfully detected! Ready to register.');
            return; // Stop the detection loop
          }
        } else {
          setScanningStatus('Position your face clearly in the frame');
        }

        if (isCapturing && detectionCount < totalDetectionsNeeded) {
          requestAnimationFrame(detectFace);
        }
      } catch (err) {
        console.error("Detection error:", err);
        setScanningStatus('Error during face detection');
      }
    };

    detectFace();
  };

  // Register face data
  const handleRegistration = async () => {
    if (!faceDescriptor || !username) {
      setError('Face data or username is missing');
      return;
    }
    
    try {
      setLoading(true);
      
      // Store face data in IndexedDB
      await storeFaceData(username, faceDescriptor);
      
      // Update UI
      setRegistrationComplete(true);
      stopVideoStream();
      setLoading(false);
      
      // Show success message
      setTimeout(() => {
        navigate('/login');
      }, 2000);
    } catch (err) {
      setError('Error registering face: ' + err.message);
      setLoading(false);
    }
  };

  // Store face data in browser's IndexedDB
  const storeFaceData = async (username, descriptor) => {
    return new Promise((resolve, reject) => {
      const request = window.indexedDB.open('FaceAuthDB', 1);
      
      request.onerror = (event) => {
        reject(new Error('Database error: ' + event.target.error));
      };
      
      request.onupgradeneeded = (event) => {
        const db = event.target.result;
        // Create object store if it doesn't exist
        if (!db.objectStoreNames.contains('faceData')) {
          const objectStore = db.createObjectStore('faceData', { keyPath: 'username' });
          objectStore.createIndex('username', 'username', { unique: true });
        }
      };
      
      request.onsuccess = (event) => {
        const db = event.target.result;
        const transaction = db.transaction(['faceData'], 'readwrite');
        const objectStore = transaction.objectStore('faceData');
        
        // Convert Float32Array to Array for storage
        const descriptorArray = Array.from(descriptor);
        
        const faceData = {
          username: username,
          descriptor: descriptorArray,
          timestamp: new Date().toISOString()
        };
        
        const addRequest = objectStore.put(faceData);
        
        addRequest.onsuccess = () => {
          resolve();
        };
        
        addRequest.onerror = (event) => {
          reject(new Error('Error storing face data: ' + event.target.error));
        };
      };
    });
  };

  // Clean up video stream
  const stopVideoStream = () => {
    if (videoRef.current?.srcObject) {
      videoRef.current.srcObject.getTracks().forEach(track => track.stop());
    }
  };

  return (
    <div className="signup-container">
      <div className="signup-card">
        <h2>Face Registration</h2>
        {error && <div className="error-message">{error}</div>}
        
        {registrationComplete ? (
          <div className="registration-success">
            <div className="success-icon">✓</div>
            <h3>Registration Complete!</h3>
            <p>Your face has been registered successfully.</p>
            <p>Redirecting to login...</p>
          </div>
        ) : (
          <div className="registration-form">
            <div className="form-group">
              <label htmlFor="username">Choose a Username</label>
              <input 
                id="username"
                type="text"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                placeholder="Enter a username"
                required
              />
            </div>
            
            <div className="face-setup">
              {isModelLoaded && !isCapturing && (
                <div className="camera-instructions">
                  <p>Enter a username and click "Start Face Registration" to begin.</p>
                  <p>Your face will be used for authentication in the future.</p>
                </div>
              )}
              
              <div className="video-wrapper">
                {!isCapturing ? (
                  <>
                    <button 
                      type="button" 
                      className={`face-capture-btn primary-btn ${username.trim() ? 'ready' : ''}`}
                      onClick={startFaceCapture}
                      disabled={!isModelLoaded || loading || !username.trim()}
                    >
                      {loading ? 'Loading Models...' : 'Start Face Registration'}
                    </button>
                    
                    {/* Add visual feedback when username is entered */}
                    {username.trim() ? (
                      <div className="username-ready">
                        Username entered: <strong>{username}</strong>. Click button above to start.
                      </div>
                    ) : (
                      <div className="camera-instructions">
                        <p>Enter a username above and click "Start Face Registration" to begin.</p>
                        <p>Your face will be used for authentication in the future.</p>
                      </div>
                    )}
                  </>
                ) : (
                  <>
                    <div className="video-container">
                      <video 
                        ref={videoRef}
                        autoPlay
                        playsInline
                        muted
                        className="face-video"
                      />
                      <canvas 
                        ref={canvasRef} 
                        className="face-overlay"
                      />
                      
                      <div className="scanning-status">
                        {scanningStatus}
                        
                        {isCapturing && !faceDetected && (
                          <div className="progress-container">
                            <div className="progress-bar" style={{width: `${detectionProgress}%`}}></div>
                          </div>
                        )}
                      </div>
                    </div>
                    
                    <div className="face-actions">
                      <button
                        onClick={() => {
                          stopVideoStream();
                          setIsCapturing(false);
                        }}
                        className="cancel-btn"
                      >
                        Cancel
                      </button>
                      
                      <button
                        onClick={handleRegistration}
                        className="register-btn"
                        disabled={!faceDetected}
                      >
                        Register Face
                      </button>
                    </div>
                    
                    <div className="face-instructions">
                      <p>Please ensure:</p>
                      <ul>
                        <li>Your face is well-lit</li>
                        <li>Look directly at the camera</li>
                        <li>Remove glasses or face coverings</li>
                      </ul>
                    </div>
                  </>
                )}
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default Signup;
