import React, { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import * as faceapi from 'face-api.js';
import { getFaceDescriptor } from './services/localStorageService';
import './Login.css';

const Login = () => {
  // Login state
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loginMethod, setLoginMethod] = useState('credentials'); // 'credentials' or 'face'
  
  // Face authentication state
  const [isCapturing, setIsCapturing] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [scanStatus, setScanStatus] = useState('');
  const [matchProgress, setMatchProgress] = useState(0);
  
  const videoRef = useRef(null);
  const navigate = useNavigate();

  // Load models when switching to face auth
  useEffect(() => {
    if (loginMethod === 'face' && !isCapturing) {
      startFaceAuthentication();
    }
  }, [loginMethod]);

  const handleCredentialLogin = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    
    try {
      // Get users from local storage
      const users = JSON.parse(localStorage.getItem('user_data') || '[]');
      const user = users.find(u => u.email === email && u.password === password);
      
      if (!user) {
        throw new Error('Invalid email or password');
      }
      
      // Store authentication info
      localStorage.setItem('authenticated', 'true');
      localStorage.setItem('authenticatedUserId', user.userId);
      localStorage.setItem('currentUser', JSON.stringify(user));
      
      navigate('/');
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const startFaceAuthentication = async () => {
    setIsCapturing(true);
    try {
      setScanStatus('Loading face detection models...');
      setLoading(true);
      
      const MODEL_URL = 'https://justadudewhohacks.github.io/face-api.js/models';
      await Promise.all([
        faceapi.nets.tinyFaceDetector.loadFromUri(MODEL_URL),
        faceapi.nets.faceLandmark68Net.loadFromUri(MODEL_URL),
        faceapi.nets.faceRecognitionNet.loadFromUri(MODEL_URL)
      ]);
      
      setScanStatus('Starting camera...');
      startVideoStream();
    } catch (err) {
      setError('Error loading face detection models: ' + err.message);
      setLoading(false);
    }
  };

  const startVideoStream = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: {} });
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        videoRef.current.onloadedmetadata = () => {
          videoRef.current.play()
            .then(() => {
              beginFaceMatching();
            })
            .catch(err => {
              setError('Error playing video: ' + err.message);
              setLoading(false);
            });
        };
      }
    } catch (err) {
      setError('Error accessing camera: ' + err.message);
      setLoading(false);
    }
  };

  const beginFaceMatching = async () => {
    setScanStatus('Looking for registered faces...');
    
    try {
      // Get all users with face auth from local storage
      const users = JSON.parse(localStorage.getItem('user_data') || '[]');
      const usersWithFace = users.filter(user => user.hasFaceAuth);
      
      if (usersWithFace.length === 0) {
        setScanStatus('No registered faces found. Please register or use email login.');
        setLoading(false);
        return;
      }
      
      startFaceDetectionLoop(usersWithFace);
    } catch (err) {
      setError('Error fetching user data: ' + err.message);
      setLoading(false);
    }
  };

  const startFaceDetectionLoop = (users) => {
    let matchAttempts = 0;
    const maxAttempts = 30;
    
    const detectFace = async () => {
      if (!videoRef.current || matchAttempts >= maxAttempts) return;
      
      try {
        setScanStatus(`Scanning for matching face (${matchAttempts + 1}/${maxAttempts})...`);
        setMatchProgress((matchAttempts / maxAttempts) * 100);
        
        const detection = await faceapi
          .detectSingleFace(videoRef.current, new faceapi.TinyFaceDetectorOptions())
          .withFaceLandmarks()
          .withFaceDescriptor();

        if (detection) {
          // Check against all users with face auth
          for (const user of users) {
            const storedDescriptor = await getFaceDescriptor(user.userId);
            
            if (storedDescriptor) {
              const distance = faceapi.euclideanDistance(detection.descriptor, storedDescriptor);
              console.log(`Match distance for user ${user.username}: ${distance}`);
              
              // Threshold for face match (lower is better)
              if (distance < 0.6) {
                setIsAuthenticated(true);
                setScanStatus(`Welcome back, ${user.username}!`);
                setLoading(false);
                
                // Store authentication info
                localStorage.setItem('authenticated', 'true');
                localStorage.setItem('authenticatedUserId', user.userId);
                localStorage.setItem('currentUser', JSON.stringify(user));
                
                setTimeout(() => {
                  stopVideoStream();
                  navigate('/');
                }, 1500);
                return;
              }
            }
          }
        }
        
        matchAttempts++;
        
        if (matchAttempts >= maxAttempts) {
          setScanStatus('Face not recognized. Please try again or use email login.');
          setLoading(false);
        } else {
          requestAnimationFrame(detectFace);
        }
      } catch (err) {
        setError('Error during face detection: ' + err.message);
        setLoading(false);
      }
    };
    
    detectFace();
  };

  const stopVideoStream = () => {
    if (videoRef.current?.srcObject) {
      videoRef.current.srcObject.getTracks().forEach(track => track.stop());
    }
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <h2>Login</h2>
        {error && <div className="error-message">{error}</div>}
        
        <div className="login-methods">
          <button 
            className={`method-btn ${loginMethod === 'credentials' ? 'active' : ''}`}
            onClick={() => {
              stopVideoStream();
              setLoginMethod('credentials');
              setIsCapturing(false);
            }}
          >
            Email & Password
          </button>
          <button 
            className={`method-btn ${loginMethod === 'face' ? 'active' : ''}`}
            onClick={() => setLoginMethod('face')}
          >
            Face ID
          </button>
        </div>
        
        {loginMethod === 'credentials' ? (
          <form onSubmit={handleCredentialLogin} className="login-form">
            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input
                id="email"
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="Enter your email"
                required
              />
            </div>
            
            <div className="form-group">
              <label htmlFor="password">Password</label>
              <input
                id="password"
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Enter your password"
                required
              />
            </div>
            
            <button 
              type="submit"
              className="login-btn"
              disabled={loading}
            >
              {loading ? 'Logging in...' : 'Login'}
            </button>
          </form>
        ) : (
          <div className="face-auth-container">
            <div className="video-container">
              <video
                ref={videoRef}
                autoPlay
                muted
                playsInline
                className="face-video"
              />
              
              {loading && (
                <div className="scanning-overlay">
                  <div className="scanning-animation"></div>
                  <p>{scanStatus}</p>
                  <div className="progress-container">
                    <div className="progress-bar" style={{width: `${matchProgress}%`}}></div>
                  </div>
                </div>
              )}
              
              {isAuthenticated && (
                <div className="auth-success-overlay">
                  <div className="success-checkmark"></div>
                  <p>Authentication Successful!</p>
                  <p className="redirect-message">Redirecting...</p>
                </div>
              )}
            </div>
            
            <div className="face-auth-instructions">
              <p>Position your face in front of the camera</p>
            </div>
          </div>
        )}

        <div className="alternative-auth">
          <p>Don't have an account?</p>
          <button 
            className="register-btn"
            onClick={() => navigate('/signup')}
          >
            Create Account
          </button>
        </div>
      </div>
    </div>
  );
};

export default Login;
