import React, { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import * as faceapi from 'face-api.js';
import { registerUser, updateFaceAuthStatus } from './firebase/authService';
import { saveFaceDescriptor } from './services/localFaceStorage';
import './Signup.css';
import { checkBrowserCompatibility, testCameraAccess } from './utils/browserCheck';

function Signup() {
  // Form state
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [username, setUsername] = useState('');
  const [formValid, setFormValid] = useState(false);
  
  // Face capture state
  const [isCapturing, setIsCapturing] = useState(false);
  const [showFaceSetup, setShowFaceSetup] = useState(false);
  const [faceDetected, setFaceDetected] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [isModelLoaded, setIsModelLoaded] = useState(false);
  const [scanningStatus, setScanningStatus] = useState('');
  const [detectionProgress, setDetectionProgress] = useState(0);
  const [browserSupport, setBrowserSupport] = useState(null);
  
  // Additional state for diagnostics
  const [cameraStatus, setCameraStatus] = useState("Not initialized");
  const [modelStatus, setModelStatus] = useState("Not loaded");
  const [diagnosticInfo, setDiagnosticInfo] = useState({});

  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const navigate = useNavigate();

  // Validate form inputs
  useEffect(() => {
    const isValid = 
      email.length > 0 && 
      email.includes('@') && 
      password.length >= 6 && 
      password === confirmPassword &&
      username.length > 0;
    
    setFormValid(isValid);
  }, [email, password, confirmPassword, username]);

  // Load face-api models
  useEffect(() => {
    if (showFaceSetup) {
      loadModels();
    }
  }, [showFaceSetup]);

  // Check browser compatibility when component mounts
  useEffect(() => {
    const compatResult = checkBrowserCompatibility();
    setBrowserSupport(compatResult);
    
    if (!compatResult.compatible) {
      setError(`Browser compatibility issues detected: ${compatResult.issues.join(", ")}`);
    }
  }, []);

  const loadModels = async () => {
    try {
      setLoading(true);
      setModelStatus("Loading models...");
      setScanningStatus('Loading face detection models...');
      
      const MODEL_URL = 'https://justadudewhohacks.github.io/face-api.js/models';
      
      // Load models one by one with status updates
      setModelStatus("Loading TinyFaceDetector...");
      await faceapi.nets.tinyFaceDetector.loadFromUri(MODEL_URL);
      
      setModelStatus("Loading FaceLandmark68...");
      await faceapi.nets.faceLandmark68Net.loadFromUri(MODEL_URL);
      
      setModelStatus("Loading FaceRecognition...");
      await faceapi.nets.faceRecognitionNet.loadFromUri(MODEL_URL);
      
      setIsModelLoaded(true);
      setModelStatus("All models loaded successfully");
      setScanningStatus('Models loaded successfully');
      setLoading(false);
    } catch (err) {
      setModelStatus(`Error loading models: ${err.message}`);
      setError('Error loading face detection models: ' + err.message);
      setLoading(false);
    }
  };

  const startFaceDetection = async () => {
    if (!videoRef.current || !canvasRef.current) return;

    const canvas = canvasRef.current;
    const video = videoRef.current;
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;

    let detectionCount = 0;
    const totalDetectionsNeeded = 20; // Number of samples for better accuracy
    
    const detectFace = async () => {
      setScanningStatus(`Analyzing face characteristics (${Math.round((detectionCount/totalDetectionsNeeded) * 100)}%)`);
      
      try {
        const detection = await faceapi
          .detectSingleFace(video, new faceapi.TinyFaceDetectorOptions())
          .withFaceLandmarks()
          .withFaceDescriptor();

        if (detection) {
          detectionCount++;
          setDetectionProgress(Math.min((detectionCount/totalDetectionsNeeded) * 100, 100));
          
          if (detectionCount >= totalDetectionsNeeded) {
            setFaceDetected(true);
            setScanningStatus('Face successfully detected! Ready to complete registration.');
          }
          
          const context = canvas.getContext('2d');
          context.clearRect(0, 0, canvas.width, canvas.height);
          
          // Draw detection results
          const dims = faceapi.matchDimensions(canvas, video, true);
          const resizedResults = faceapi.resizeResults(detection, dims);
          faceapi.draw.drawDetections(canvas, [resizedResults]);
          faceapi.draw.drawFaceLandmarks(canvas, [resizedResults]);
        } else {
          setScanningStatus('Position your face clearly in the frame');
        }

        if (isCapturing && detectionCount < totalDetectionsNeeded) {
          requestAnimationFrame(detectFace);
        }
      } catch (err) {
        console.error("Detection error:", err);
        setScanningStatus('Error during face detection');
      }
    };

    detectFace();
  };

  const startFaceCapture = async () => {
    setIsCapturing(true);
    setCameraStatus("Initializing camera...");
    
    // Check browser compatibility again
    if (browserSupport && !browserSupport.compatible) {
      setError(`Your browser may not fully support face detection: ${browserSupport.issues.join(", ")}`);
    }
    
    // Test camera access before attempting to use it
    const cameraTest = await testCameraAccess();
    if (!cameraTest.success) {
      if (cameraTest.isDenied) {
        setCameraStatus("Camera permission denied");
        setError("Camera permission denied. Please allow camera access and try again.");
      } else if (cameraTest.isNotFound) {
        setCameraStatus("No camera found");
        setError("No camera found. Please connect a camera and try again.");
      } else {
        setCameraStatus(`Camera error: ${cameraTest.message}`);
        setError(`Camera error: ${cameraTest.message}`);
      }
      setIsCapturing(false);
      return;
    }
    
    try {
      console.log("Requesting camera access...");
      setCameraStatus("Requesting camera access...");
      
      // Explicitly set video constraints for better compatibility
      const constraints = { 
        video: {
          facingMode: "user",
          width: { ideal: 640, min: 320 },
          height: { ideal: 480, min: 240 }
        } 
      };
      
      const stream = await navigator.mediaDevices.getUserMedia(constraints);
      console.log("Camera stream obtained:", stream);
      setCameraStatus("Camera stream obtained");
      
      // Update diagnostic info
      setDiagnosticInfo({
        streamActive: stream.active,
        streamId: stream.id,
        videoTracks: stream.getVideoTracks().map(track => ({
          label: track.label,
          enabled: track.enabled,
          muted: track.muted
        }))
      });
      
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        console.log("Set stream to video element");
        setCameraStatus("Stream connected to video element");
        
        // Force video element to be visible
        videoRef.current.style.display = "block";
        videoRef.current.style.width = "100%";
        videoRef.current.style.height = "auto";
        videoRef.current.style.maxHeight = "400px";
        videoRef.current.style.objectFit = "cover";
        videoRef.current.style.backgroundColor = "#f0f0f0";
        videoRef.current.style.border = "2px solid teal";
        
        videoRef.current.addEventListener('loadedmetadata', () => {
          console.log("Video metadata loaded", videoRef.current.videoWidth, videoRef.current.videoHeight);
          setCameraStatus("Video metadata loaded");
          
          // Force proper dimensions once metadata is loaded
          if (canvasRef.current) {
            canvasRef.current.width = videoRef.current.videoWidth;
            canvasRef.current.height = videoRef.current.videoHeight;
          }
        });
        
        videoRef.current.addEventListener('playing', () => {
          console.log("Video is playing");
          setCameraStatus("Video is playing");
        });
        
        videoRef.current.addEventListener('error', (e) => {
          console.error("Video error:", e);
          setCameraStatus(`Video error: ${e.type}`);
        });
        
        videoRef.current.onloadedmetadata = () => {
          console.log("Metadata loaded, attempting to play video");
          setCameraStatus("Metadata loaded, attempting to play");
          
          videoRef.current.play()
            .then(() => {
              console.log("Video playback started successfully");
              setCameraStatus("Video playback started successfully");
              startFaceDetection();
            })
            .catch(err => {
              console.error("Error playing video:", err);
              setCameraStatus(`Error playing video: ${err.message}`);
              setError(`Failed to play video: ${err.message}. Please try using Chrome or Edge.`);
            });
        };
      } else {
        console.error("Video reference is not available");
        setCameraStatus("Video reference is not available");
        setError("Video element not initialized. Please refresh the page.");
      }
    } catch (err) {
      console.error("Camera access error:", err);
      setCameraStatus(`Camera access error: ${err.name} - ${err.message}`);
      setError(`Camera access denied: ${err.message}. Please ensure camera permissions are granted.`);
    }
  };

  // Modified handleSignup to store face data in a folder
  const handleSignup = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    try {
      // Register the user with Firebase
      const userData = await registerUser(email, password, username, false);
      const userId = userData.userId;
      
      // If face detection is complete, save the face data locally
      if (faceDetected && videoRef.current) {
        console.log("Saving face descriptor for user:", userId);
        const detection = await faceapi
          .detectSingleFace(videoRef.current, new faceapi.TinyFaceDetectorOptions())
          .withFaceLandmarks()
          .withFaceDescriptor();

        if (detection) {
          console.log("Face detection successful, saving descriptor");
          
          // 1. Save face data to localStorage for immediate use
          const success = await saveFaceDescriptor(userId, detection.descriptor);
          
          // 2. Save a snapshot image to the faceData folder
          await saveFaceImage(userId, username, videoRef.current, detection);
          
          if (success) {
            // Update Firebase to indicate this user has face auth enabled
            await updateFaceAuthStatus(userId, true);
            console.log("Face descriptor saved successfully and status updated in Firebase");
          } else {
            console.error("Failed to save face descriptor");
            throw new Error('Failed to save face ID data');
          }
        } else {
          console.error("No face detected in final verification");
          throw new Error('No face detected in final verification');
        }
      }
      
      // Clean up camera stream
      if (videoRef.current?.srcObject) {
        console.log("Stopping video stream");
        stopVideoStream();
      }
      
      // Show success message
      alert('Account created successfully! You can now log in.');
      navigate('/login');
    } catch (err) {
      console.error('Signup error:', err);
      setError('Error creating account: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  // New function to save face image
  const saveFaceImage = async (userId, username, videoElement, detection) => {
    try {
      // Create canvas to capture the face
      const canvas = document.createElement('canvas');
      const context = canvas.getContext('2d');
      
      // Set canvas size to video dimensions
      canvas.width = videoElement.videoWidth;
      canvas.height = videoElement.videoHeight;
      
      // Draw the current video frame to the canvas
      context.drawImage(videoElement, 0, 0, canvas.width, canvas.height);
      
      // Draw face detection box
      if (detection) {
        const box = detection.detection.box;
        context.strokeStyle = 'teal';
        context.lineWidth = 2;
        context.strokeRect(box.x, box.y, box.width, box.height);
      }
      
      // Convert canvas to blob
      const imageBlob = await new Promise(resolve => {
        canvas.toBlob(blob => resolve(blob), 'image/jpeg', 0.95);
      });
      
      // Create a downloadable link for the face image
      const downloadLink = document.createElement('a');
      downloadLink.href = URL.createObjectURL(imageBlob);
      downloadLink.download = `faceData_${username}_${userId}.jpg`;
      downloadLink.style.display = 'none';
      document.body.appendChild(downloadLink);
      downloadLink.click();
      document.body.removeChild(downloadLink);
      
      console.log(`Face image saved for user: ${username}`);
      return true;
    } catch (error) {
      console.error('Error saving face image:', error);
      return false;
    }
  };
  
  const stopVideoStream = () => {
    if (videoRef.current?.srcObject) {
      videoRef.current.srcObject.getTracks().forEach(track => track.stop());
    }
  };

  const skipFaceSetup = () => {
    if (window.confirm('Are you sure you want to skip Face ID setup? You can still log in with email and password.')) {
      setShowFaceSetup(false);
      setFaceDetected(false);
      stopVideoStream();
    }
  };

  return (
    <div className="signup-container">
      <div className="signup-card">
        <h2>Create Account</h2>
        
        {error && <div className="error-message">{error}</div>}
        
        {!showFaceSetup ? (
          // Email/password registration form
          <form onSubmit={(e) => {
            e.preventDefault();
            if (formValid) {
              setShowFaceSetup(true);
            }
          }}>
            <div className="form-group">
              <label htmlFor="username">Username</label>
              <input
                id="username"
                type="text"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                placeholder="Choose a username"
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="email">Email Address</label>
              <input
                id="email"
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="Enter your email"
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="password">Password</label>
              <input
                id="password"
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Create a password"
                required
                minLength={6}
              />
              {password.length > 0 && password.length < 6 && 
                <div className="input-hint">Password must be at least 6 characters</div>
              }
            </div>
            <div className="form-group">
              <label htmlFor="confirmPassword">Confirm Password</label>
              <input
                id="confirmPassword"
                type="password"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
                placeholder="Confirm your password"
                required
              />
              {confirmPassword.length > 0 && confirmPassword !== password && 
                <div className="input-hint">Passwords don't match</div>
              }
            </div>

            <button 
              type="submit"
              className="signup-btn continue-btn"
              disabled={!formValid}
            >
              Continue
            </button>
            
            <div className="login-link">
              Already have an account? <a href="/login">Sign in</a>
            </div>
          </form>
        ) : (
          <div className="face-setup">
            <div className="setup-header">
              <h3>Set Up Face ID (Optional)</h3>
              <p className="setup-description">
                Add Face ID to enable quick and secure login without typing your password.
              </p>
            </div>
            
            {/* Status indicators */}
            <div className="setup-status">
              <div className="status-item">
                <span className="status-label">Camera:</span>
                <span className={`status-value ${cameraStatus.includes("error") || cameraStatus.includes("denied") ? "status-error" : ""}`}>
                  {cameraStatus}
                </span>
              </div>
              <div className="status-item">
                <span className="status-label">Models:</span>
                <span className={`status-value ${modelStatus.includes("Error") ? "status-error" : ""}`}>
                  {modelStatus}
                </span>
              </div>
            </div>
            
            <div className="video-wrapper">
              {!isCapturing ? (
                <>
                  <button 
                    type="button" 
                    className="face-capture-btn"
                    onClick={startFaceCapture}
                    disabled={!isModelLoaded || loading}
                  >
                    {loading ? 'Loading Models...' : 'Enable Face ID'}
                  </button>
                  
                  {loading && (
                    <div className="model-loading-progress">
                      <div className="progress-bar-container">
                        <div className="progress-bar" style={{width: '100%'}}></div>
                      </div>
                      <p className="scanning-status">{scanningStatus}</p>
                    </div>
                  )}
                </>
              ) : (
                <>
                  <div className="video-container" style={{
                    position: 'relative',
                    width: '100%',
                    height: '400px',
                    overflow: 'hidden',
                    borderRadius: '12px',
                    backgroundColor: '#f0f0f0',
                    border: '2px solid teal',
                    margin: '20px 0'
                  }}>
                    <video
                      ref={videoRef}
                      autoPlay
                      playsInline
                      muted
                      width="640"
                      height="480"
                      style={{
                        width: '100%',
                        height: '100%',
                        objectFit: 'cover',
                        display: 'block',
                        zIndex: 1
                      }}
                    />
                    <canvas 
                      ref={canvasRef} 
                      className="face-overlay" 
                      style={{
                        position: 'absolute',
                        top: 0,
                        left: 0,
                        width: '100%',
                        height: '100%',
                        zIndex: 2
                      }}
                    />
                    {faceDetected && <div className="face-detected-indicator" />}
                    
                    {/* Camera feed indicator */}
                    <div className="camera-indicator">
                      {videoRef.current?.srcObject ? 'Camera active' : 'Initializing camera...'}
                    </div>
                  </div>
                  
                  <div className="detection-status">
                    <div className="progress-bar-container">
                      <div className="progress-bar" style={{width: `${detectionProgress}%`}}></div>
                    </div>
                    <p className="scanning-status">{scanningStatus || "Waiting for face detection..."}</p>
                    <p className="face-status">{faceDetected ? 'Face Detected ✓' : 'No Face Detected'}</p>
                  </div>
                </>
              )}
            </div>

            <div className="face-setup-actions">
              <button
                className="skip-btn"
                type="button"
                onClick={skipFaceSetup}
              >
                Skip Face ID
              </button>
              
              <button 
                onClick={handleSignup}
                className="signup-btn"
                disabled={loading || (isCapturing && !faceDetected)}
              >
                {loading ? 'Registering...' : isCapturing && !faceDetected ? 'Waiting for Face...' : 'Complete Registration'}
              </button>
            </div>

            {isCapturing && (
              <div className="signup-help">
                <p>Please ensure:</p>
                <ul>
                  <li>Your face is well-lit</li>
                  <li>Look directly at the camera</li>
                  <li>Remove glasses or face coverings</li>
                  <li>Keep your face still during scanning</li>
                </ul>
              </div>
            )}
            
            {/* Diagnostic information - hidden in production */}
            {Object.keys(diagnosticInfo).length > 0 && (
              <div className="diagnostic-info">
                <details>
                  <summary>Camera Diagnostics</summary>
                  <pre>{JSON.stringify(diagnosticInfo, null, 2)}</pre>
                </details>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default Signup;
