import React, { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";
import { FaUser, FaLock, FaIdCard, FaMapMarkerAlt, FaPhone } from "react-icons/fa";
import { createUserWithEmailAndPassword } from "firebase/auth";
import { doc, setDoc } from "firebase/firestore";
import { auth, db } from "./firebase/config";

const AshaWorkerSignup = () => {
  const [formData, setFormData] = useState({
    fullName: "",
    workerId: "",
    district: "",
    phoneNumber: "",
    email: "",
    password: "",
    confirmPassword: "",
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // Check if already logged in
  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged((user) => {
      if (user && sessionStorage.getItem('userRole') === 'ashaWorker') {
        navigate("/asha-dashboard");
      }
    });

    return () => unsubscribe();
  }, [navigate]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSignup = async (e) => {
    e.preventDefault();
    
    // Basic validation
    if (formData.password !== formData.confirmPassword) {
      setError("Passwords don't match");
      return;
    }
    
    if (formData.password.length < 6) {
      setError("Password must be at least 6 characters");
      return;
    }
    
    if (formData.phoneNumber.length !== 10 || isNaN(Number(formData.phoneNumber))) {
      setError("Please enter a valid 10-digit phone number");
      return;
    }
    
    setLoading(true);
    setError(null);

    try {
      // Create user in Firebase Auth
      const userCredential = await createUserWithEmailAndPassword(
        auth, 
        formData.email, 
        formData.password
      );
      
      const user = userCredential.user;
      
      // Save additional user data in Firestore
      await setDoc(doc(db, "ashaWorkers", user.uid), {
        fullName: formData.fullName,
        workerId: formData.workerId,
        district: formData.district,
        phoneNumber: formData.phoneNumber,
        email: formData.email,
        createdAt: new Date().toISOString(),
        role: "ashaWorker"
      });
      
      // Set session data
      sessionStorage.setItem('userRole', 'ashaWorker');
      sessionStorage.setItem('userEmail', formData.email);
      
      console.log("Asha worker account created successfully!");
      navigate("/asha-dashboard");
      
    } catch (error) {
      console.error("Signup error:", error);
      setError(getErrorMessage(error.code));
    } finally {
      setLoading(false);
    }
  };

  // Helper function to provide user-friendly error messages
  const getErrorMessage = (errorCode) => {
    switch (errorCode) {
      case 'auth/email-already-in-use':
        return 'This email is already registered.';
      case 'auth/invalid-email':
        return 'Invalid email address format.';
      case 'auth/weak-password':
        return 'Password is too weak. Use at least 6 characters.';
      default:
        return 'Failed to create account. Please try again.';
    }
  };

  return (
    <div
      style={{
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        minHeight: "100vh",
        backgroundImage: "url('https://via.placeholder.com/1920x1080?text=Healthcare+Background')",
        backgroundSize: "cover",
        backgroundPosition: "center",
        padding: "20px 0",
      }}
    >
      <div
        style={{
          backgroundColor: "rgba(255, 255, 255, 0.8)",
          padding: "2.5rem",
          borderRadius: "15px",
          boxShadow: "0 8px 20px rgba(0, 0, 0, 0.2)",
          width: "500px",
          textAlign: "center",
          backdropFilter: "blur(10px)",
          border: "1px solid rgba(255, 255, 255, 0.3)",
          transition: "transform 0.3s ease, box-shadow 0.3s ease",
        }}
        onMouseOver={(e) => {
          e.currentTarget.style.transform = "scale(1.02)";
          e.currentTarget.style.boxShadow = "0 12px 24px rgba(0, 0, 0, 0.3)";
        }}
        onMouseOut={(e) => {
          e.currentTarget.style.transform = "scale(1)";
          e.currentTarget.style.boxShadow = "0 8px 20px rgba(0, 0, 0, 0.2)";
        }}
      >
        <h2
          style={{
            fontSize: "2rem",
            fontWeight: "bold",
            marginBottom: "1.5rem",
            color: "#2d3748",
            textShadow: "2px 2px 4px rgba(0, 0, 0, 0.1)",
          }}
        >
          Asha Worker Registration
        </h2>
        
        {error && (
          <div style={{
            padding: "10px",
            marginBottom: "15px",
            backgroundColor: "rgba(254, 215, 215, 0.8)",
            color: "#c53030",
            borderRadius: "8px",
            fontSize: "0.9rem"
          }}>
            {error}
          </div>
        )}
        
        <form onSubmit={handleSignup}>
          <div style={{ marginBottom: "1rem", position: "relative" }}>
            <label
              style={{
                display: "block",
                fontSize: "0.875rem",
                fontWeight: "medium",
                marginBottom: "0.5rem",
                color: "#4a5568",
                textAlign: "left"
              }}
            >
              Full Name
            </label>
            <div style={{ position: "relative" }}>
              <FaUser
                style={{
                  position: "absolute",
                  left: "10px",
                  top: "50%",
                  transform: "translateY(-50%)",
                  color: "#718096",
                }}
              />
              <input
                type="text"
                name="fullName"
                value={formData.fullName}
                onChange={handleChange}
                style={{
                  width: "100%",
                  padding: "0.5rem 0.5rem 0.5rem 2.5rem",
                  border: "1px solid #e2e8f0",
                  borderRadius: "0.375rem",
                  outline: "none",
                }}
                required
              />
            </div>
          </div>
          
          <div style={{ marginBottom: "1rem", position: "relative" }}>
            <label
              style={{
                display: "block",
                fontSize: "0.875rem",
                fontWeight: "medium",
                marginBottom: "0.5rem",
                color: "#4a5568",
                textAlign: "left"
              }}
            >
              Worker ID
            </label>
            <div style={{ position: "relative" }}>
              <FaIdCard
                style={{
                  position: "absolute",
                  left: "10px",
                  top: "50%",
                  transform: "translateY(-50%)",
                  color: "#718096",
                }}
              />
              <input
                type="text"
                name="workerId"
                value={formData.workerId}
                onChange={handleChange}
                style={{
                  width: "100%",
                  padding: "0.5rem 0.5rem 0.5rem 2.5rem",
                  border: "1px solid #e2e8f0",
                  borderRadius: "0.375rem",
                  outline: "none",
                }}
                required
              />
            </div>
          </div>
          
          <div style={{ marginBottom: "1rem", position: "relative" }}>
            <label
              style={{
                display: "block",
                fontSize: "0.875rem",
                fontWeight: "medium",
                marginBottom: "0.5rem",
                color: "#4a5568",
                textAlign: "left"
              }}
            >
              District
            </label>
            <div style={{ position: "relative" }}>
              <FaMapMarkerAlt
                style={{
                  position: "absolute",
                  left: "10px",
                  top: "50%",
                  transform: "translateY(-50%)",
                  color: "#718096",
                }}
              />
              <input
                type="text"
                name="district"
                value={formData.district}
                onChange={handleChange}
                style={{
                  width: "100%",
                  padding: "0.5rem 0.5rem 0.5rem 2.5rem",
                  border: "1px solid #e2e8f0",
                  borderRadius: "0.375rem",
                  outline: "none",
                }}
                required
              />
            </div>
          </div>
          
          <div style={{ marginBottom: "1rem", position: "relative" }}>
            <label
              style={{
                display: "block",
                fontSize: "0.875rem",
                fontWeight: "medium",
                marginBottom: "0.5rem",
                color: "#4a5568",
                textAlign: "left"
              }}
            >
              Phone Number
            </label>
            <div style={{ position: "relative" }}>
              <FaPhone
                style={{
                  position: "absolute",
                  left: "10px",
                  top: "50%",
                  transform: "translateY(-50%)",
                  color: "#718096",
                }}
              />
              <input
                type="tel"
                name="phoneNumber"
                value={formData.phoneNumber}
                onChange={handleChange}
                style={{
                  width: "100%",
                  padding: "0.5rem 0.5rem 0.5rem 2.5rem",
                  border: "1px solid #e2e8f0",
                  borderRadius: "0.375rem",
                  outline: "none",
                }}
                required
              />
            </div>
          </div>
          
          <div style={{ marginBottom: "1rem", position: "relative" }}>
            <label
              style={{
                display: "block",
                fontSize: "0.875rem",
                fontWeight: "medium",
                marginBottom: "0.5rem",
                color: "#4a5568",
                textAlign: "left"
              }}
            >
              Email
            </label>
            <div style={{ position: "relative" }}>
              <FaUser
                style={{
                  position: "absolute",
                  left: "10px",
                  top: "50%",
                  transform: "translateY(-50%)",
                  color: "#718096",
                }}
              />
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                style={{
                  width: "100%",
                  padding: "0.5rem 0.5rem 0.5rem 2.5rem",
                  border: "1px solid #e2e8f0",
                  borderRadius: "0.375rem",
                  outline: "none",
                }}
                required
              />
            </div>
          </div>
          
          <div style={{ marginBottom: "1rem", position: "relative" }}>
            <label
              style={{
                display: "block",
                fontSize: "0.875rem",
                fontWeight: "medium",
                marginBottom: "0.5rem",
                color: "#4a5568",
                textAlign: "left"
              }}
            >
              Password
            </label>
            <div style={{ position: "relative" }}>
              <FaLock
                style={{
                  position: "absolute",
                  left: "10px",
                  top: "50%",
                  transform: "translateY(-50%)",
                  color: "#718096",
                }}
              />
              <input
                type="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                style={{
                  width: "100%",
                  padding: "0.5rem 0.5rem 0.5rem 2.5rem",
                  border: "1px solid #e2e8f0",
                  borderRadius: "0.375rem",
                  outline: "none",
                }}
                required
              />
            </div>
          </div>
          
          <div style={{ marginBottom: "1.5rem", position: "relative" }}>
            <label
              style={{
                display: "block",
                fontSize: "0.875rem",
                fontWeight: "medium",
                marginBottom: "0.5rem",
                color: "#4a5568",
                textAlign: "left"
              }}
            >
              Confirm Password
            </label>
            <div style={{ position: "relative" }}>
              <FaLock
                style={{
                  position: "absolute",
                  left: "10px",
                  top: "50%",
                  transform: "translateY(-50%)",
                  color: "#718096",
                }}
              />
              <input
                type="password"
                name="confirmPassword"
                value={formData.confirmPassword}
                onChange={handleChange}
                style={{
                  width: "100%",
                  padding: "0.5rem 0.5rem 0.5rem 2.5rem",
                  border: "1px solid #e2e8f0",
                  borderRadius: "0.375rem",
                  outline: "none",
                }}
                required
              />
            </div>
          </div>
          
          <button
            type="submit"
            disabled={loading}
            style={{
              width: "100%",
              backgroundColor: loading ? "#90cdf4" : "#4299e1",
              color: "white",
              padding: "0.75rem",
              borderRadius: "0.375rem",
              fontWeight: "bold",
              cursor: loading ? "not-allowed" : "pointer",
              transition: "background-color 0.2s, transform 0.2s",
              border: "none",
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
              marginTop: "1.5rem"
            }}
            onMouseOver={(e) => {
              if (!loading) {
                e.target.style.backgroundColor = "#3182ce";
                e.target.style.transform = "translateY(-2px)";
              }
            }}
            onMouseOut={(e) => {
              if (!loading) {
                e.target.style.backgroundColor = "#4299e1";
                e.target.style.transform = "translateY(0)";
              }
            }}
          >
            {loading ? "Creating Account..." : "Sign Up"}
          </button>
        </form>
        
        <div style={{ marginTop: "20px", color: "#4a5568" }}>
          <p>Already have an account? <Link to="/asha-login" style={{ color: "#4299e1", textDecoration: "none", fontWeight: "500" }}>Login</Link></p>
        </div>
      </div>
    </div>
  );
};

export default AshaWorkerSignup;
