import React, { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import * as faceapi from 'face-api.js';
import './Signup.css';

function Signup() {
  const [username, setUsername] = useState('');
  const [faceDetected, setFaceDetected] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [isModelLoaded, setIsModelLoaded] = useState(false);
  const [isCapturing, setIsCapturing] = useState(false);
  const [scanMessage, setScanMessage] = useState('');
  const [capturedDescriptor, setCapturedDescriptor] = useState(null);
  const [videoStream, setVideoStream] = useState(null);

  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const navigate = useNavigate();

  // Load face-api models
  useEffect(() => {
    const loadModels = async () => {
      try {
        setLoading(true);
        const MODEL_URL = 'https://justadudewhohacks.github.io/face-api.js/models';
        await Promise.all([
          faceapi.nets.tinyFaceDetector.loadFromUri(MODEL_URL),
          faceapi.nets.faceLandmark68Net.loadFromUri(MODEL_URL),
          faceapi.nets.faceRecognitionNet.loadFromUri(MODEL_URL)
        ]);
        setIsModelLoaded(true);
        setLoading(false);
      } catch (err) {
        setError('Error loading face detection models: ' + err.message);
        setLoading(false);
      }
    };
    loadModels();
  }, []);

  const startCamera = async () => {
    if (!username.trim()) {
      setError('Please enter a username first');
      return;
    }

    try {
      setIsCapturing(true);
      setError('');
      const stream = await navigator.mediaDevices.getUserMedia({ 
        video: {
          width: { ideal: 640 },
          height: { ideal: 480 },
          facingMode: 'user'
        }
      });
      
      setVideoStream(stream);
      
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        videoRef.current.onloadedmetadata = () => {
          videoRef.current.play().catch(err => {
            console.error("Error playing video:", err);
            setError("Error starting video playback");
          });
        };
      }
    } catch (err) {
      console.error("Camera access error:", err);
      setError(`Camera access failed: ${err.message}`);
      setIsCapturing(false);
    }
  };

  useEffect(() => {
    return () => {
      if (videoStream) {
        videoStream.getTracks().forEach(track => track.stop());
      }
    };
  }, [videoStream]);

  const stopCamera = () => {
    if (videoStream) {
      videoStream.getTracks().forEach(track => track.stop());
      setVideoStream(null);
    }
    if (videoRef.current) {
      videoRef.current.srcObject = null;
    }
    setIsCapturing(false);
  };

  const saveFaceData = async () => {
    if (!capturedDescriptor || !username) {
      setError('Face data or username missing');
      return;
    }

    try {
      // Store in localStorage
      const faceData = {
        username,
        descriptor: Array.from(capturedDescriptor),
        timestamp: new Date().toISOString()
      };

      const existingData = JSON.parse(localStorage.getItem('faceData') || '[]');
      existingData.push(faceData);
      localStorage.setItem('faceData', JSON.stringify(existingData));

      stopCamera();
      alert('Face registered successfully!');
      navigate('/login');
    } catch (err) {
      setError('Failed to save face data: ' + err.message);
    }
  };

  const detectFace = async () => {
    if (!videoRef.current || !canvasRef.current) return;

    const video = videoRef.current;
    const canvas = canvasRef.current;

    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;

    setScanMessage('Analyzing face...');

    try {
      const detection = await faceapi
        .detectSingleFace(video, new faceapi.TinyFaceDetectorOptions())
        .withFaceLandmarks()
        .withFaceDescriptor();

      if (detection) {
        // Draw the detection
        const context = canvas.getContext('2d');
        context.clearRect(0, 0, canvas.width, canvas.height);
        
        const dims = faceapi.matchDimensions(canvas, video, true);
        const resizedResults = faceapi.resizeResults(detection, dims);
        faceapi.draw.drawDetections(canvas, [resizedResults]);
        faceapi.draw.drawFaceLandmarks(canvas, [resizedResults]);

        setCapturedDescriptor(detection.descriptor);
        setFaceDetected(true);
        setScanMessage('Face detected! Click Register to save.');
      } else {
        setScanMessage('No face detected. Please position your face in the frame.');
        setFaceDetected(false);
      }

      if (isCapturing) {
        requestAnimationFrame(detectFace);
      }
    } catch (err) {
      setScanMessage('Error during detection: ' + err.message);
    }
  };

  useEffect(() => {
    let animationFrameId;
    
    if (isCapturing && videoRef.current && videoStream) {
      const detectFace = async () => {
        if (!videoRef.current || !canvasRef.current) return;

        const video = videoRef.current;
        const canvas = canvasRef.current;

        canvas.width = video.videoWidth;
        canvas.height = video.videoHeight;

        setScanMessage('Analyzing face...');

        try {
          const detection = await faceapi
            .detectSingleFace(video, new faceapi.TinyFaceDetectorOptions())
            .withFaceLandmarks()
            .withFaceDescriptor();

          if (detection) {
            // Draw the detection
            const context = canvas.getContext('2d');
            context.clearRect(0, 0, canvas.width, canvas.height);
            
            const dims = faceapi.matchDimensions(canvas, video, true);
            const resizedResults = faceapi.resizeResults(detection, dims);
            faceapi.draw.drawDetections(canvas, [resizedResults]);
            faceapi.draw.drawFaceLandmarks(canvas, [resizedResults]);

            setCapturedDescriptor(detection.descriptor);
            setFaceDetected(true);
            setScanMessage('Face detected! Click Register to save.');
          } else {
            setScanMessage('No face detected. Please position your face in the frame.');
            setFaceDetected(false);
          }

          if (isCapturing) {
            animationFrameId = requestAnimationFrame(detectFace);
          }
        } catch (err) {
          setScanMessage('Error during detection: ' + err.message);
        }
      };

      videoRef.current.onplay = () => {
        detectFace();
      };
    }

    return () => {
      if (animationFrameId) {
        cancelAnimationFrame(animationFrameId);
      }
    };
  }, [isCapturing, videoStream]);

  return (
    <div className="signup-simple">
      <div className="signup-card">
        <h2>Face Registration</h2>
        {error && <div className="error-message">{error}</div>}

        <div className="input-group">
          <input
            type="text"
            placeholder="Enter your username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            disabled={isCapturing}
          />
        </div>

        <div className="camera-section">
          {!isCapturing ? (
            <button
              className="start-button"
              onClick={startCamera}
              disabled={loading || !isModelLoaded || !username.trim()}
            >
              {loading ? 'Loading...' : 'Start Camera'}
            </button>
          ) : (
            <div className="camera-container">
              <video
                ref={videoRef}
                autoPlay
                playsInline
                muted
                style={{ display: 'block', width: '100%' }}
              />
              <canvas 
                ref={canvasRef}
                style={{
                  position: 'absolute',
                  top: 0,
                  left: 0,
                  width: '100%',
                  height: '100%'
                }}
              />
              <div className="scan-message">{scanMessage}</div>
            </div>
          )}
        </div>

        {isCapturing && (
          <div className="button-group">
            <button className="cancel-button" onClick={stopCamera}>
              Cancel
            </button>
            <button
              className="register-button"
              onClick={saveFaceData}
              disabled={!faceDetected}
            >
              Register Face
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

export default Signup;
