import { initializeApp } from "firebase/app";
import { getAuth } from "firebase/auth";
import { getDatabase, connectDatabaseEmulator } from "firebase/database";
import { getStorage } from "firebase/storage";
import { getFirestore } from "firebase/firestore";

// Update with the correct Firebase configuration provided
const firebaseConfig = {
  apiKey: "AIzaSyB_Eaqt3c_xG-_TFi6P74qZwrolzp7Jgxs",
  authDomain: "mediconnect-e34fc.firebaseapp.com",
  databaseURL: "https://mediconnect-e34fc-default-rtdb.firebaseio.com",
  projectId: "mediconnect-e34fc",
  storageBucket: "mediconnect-e34fc.firebasestorage.app",
  messagingSenderId: "871216482812",
  appId: "1:871216482812:web:781197acbb9b72ba894a79",
  measurementId: "G-8TEH9PTNJL"
};

// Initialize Firebase
console.log("Initializing Firebase with config:", {
  projectId: firebaseConfig.projectId,
  databaseURL: firebaseConfig.databaseURL,
  // Don't log sensitive information like the API key
});

const app = initializeApp(firebaseConfig);

// Get Firebase services
export const auth = getAuth(app);
export const db = getFirestore(app);
export const rtdb = getDatabase(app);
export const storage = getStorage(app);

// Monitor database connection state
if (rtdb) {
  const { ref, onValue } = require('firebase/database');
  const connectedRef = ref(rtdb, '.info/connected');
  onValue(connectedRef, (snap) => {
    if (snap.val() === true) {
      console.log("Connected to Firebase Realtime Database");
    } else {
      console.warn("Disconnected from Firebase Realtime Database");
    }
  });
}

export default app;
